INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (1, 'YTH', '', 'Описание Коньки BAUER SUPREME S140 YTH, детская, любительская модель в новой линейке Supreme 2016 года. Внешнее материал ботинка выполнен из нейлона и отличается высокой стойкостью к механическим нагрузкам, истиранию и порезам. Ботинок имеет оптимальный для новичка уровень жесткости и обеспечивает необходимую стабильность и устойчивость. Внутренняя обивка в области голеностопного сустава с интегрированной анатомической пяточной чашей создает необходимую поддержку и плотно фиксирует стопу внутри ботинка. Внутренний язычок анатомической конструкции удобно охватывает переднюю часть голеностопа, обеспечивая необходимую защиту. Внутренняя футеровка язычка выполнена из белого войлока плотностью 30 унций. Внутренняя стелька из формованной, легкой пены EVA способствует удобному положению стопы внутри ботинка, обеспечивает хорошую теплоизоляцию. Внутренняя подкладка из микрофибры отличается высокой износостойкостью и мягкостью. Подошва выполнена из термопластичной резины. На коньках этой модели установлены прочные и легкие стаканы Tuuk Lightspeed Pro. Несменные лезвия Tuuk Super Stainless Steel выполнены из нержавеющей стали. Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME S140_YTH.png', null, 'SUPREME S140', 1, 1);
