INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (3, 'YTH', '', 'Описание Коньки BAUER VAPOR S17 X300 - это любительская модель в линейке VAPOR 2017 года. Похоже, некоторые игроки просто знают, куда полетит шайба. Такие игроки всегда на один шаг опережают своих соперников — они находятся в эпицентре момента, а не наблюдают со стороны. Если это про вас, проверьте коньки X300 с композитными интегрированными вставками из волокна quarter fit. Они быстрые. Маневренные. И готовы к игре. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитные интегрированные вставки из волокна quarter fit • Материал подкладки - Микрофибра • Защита лодыжки - Подушки для лодыжки из анатомического пеноматериала. • Конструкция языка - Анатомической формы, из двух частей, из войлока • Стелька - Стандартный комфорт • Термоформуемость - Терморегулируемый верх НИЖНЯЯ ЧАСТЬ: • Стакан (держатель для лезвия) - TUUK LIGHTSPEED Pro • Лезвие - Нержавеющая сталь TUUK Характеристики Возрастная группа YTH Линейка VAPOR', '/root/images/Коньки/BAUER_VAPOR S17 X300_YTH.png', null, 'VAPOR S17 X300', 1, 1);
