INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (13, 'JR', '', 'Описание Коньки BAUER SUPREME S160, полупрофессиональная модель в новой линейке SUPREME 2016 года. Ботинок коньков имеет анатомическую форму и облегает ногу, образуя удобную и естественную посадку. Внешнее покрытие выполнено из материала 3D TrueForm, армированного слоем Tech PU, что обеспечивает повышенную жесткость ботинка и оптимальную устойчивость. Данный материал термоформируемый и после процедуры термоформовки создает персонализированную посадку. Наполнение в области голеностопного сустава выполнено из легкой пены с эффектом памяти и быстро принимает удобную, анатомически правильную форму. Внешняя, усиленная пяточная чаша обеспечивает дополнительную защиту заднего отдела стопы. Внутренний войлочный язычок имеет анатомическую конструкцию, состоит из двух частей, толщиной 40 oz, с высокопрочной защитой. Внутреннее наполнение язычка из пены высокой плотности защищает подъем стопы и переднюю часть голеностопного сустава от передавливания шнуровкой и ударов шайбой, клюшкой или коньком. Внутренняя стелька Form-Fit EVAспособствует правильному положению стопы внутри ботинка. Подошва производится из термопластичной резины. Стакан Tuuk LightSpeed Edge имеет систему быстрой замены лезвий Trigger System. Лезвия Tuuk LightSpeed Super. Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME S160_JR.png', null, 'SUPREME S160', 1, 1);
