INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (15, 'JR', '', 'Описание Коньки BAUER NEXUS N7000 полупрофессиональная модель в линейке Nexus 2016 года. Управляйте игрой с помощью комфортных, надежных и легких ботинок и стельки FORM-FIT со стабилизирующим захватом. С быстросъемной системой для лезвий LIGHTSPEED EDGE вы никогда не пропустите смену. Особенности: • Верх ботинка из технического сетчатого нейлона. • Влагоотталкивающая подкладка из микроволокна. • Яычок анатомической формы 40oz из 2 частей с внедренной плюсневой защитой из пенополиэтилена. • Ультралегкие пенные вставки в области лодыжки. • Внутренняя стелька Form-Fit с нескользящим стабилизирующим покрытием. • Стакан Tuuk Lightspeed Edge с лезвием из нержавеющей стали Tuuk Super. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Коньки/BAUER_NEXUS N7000_JR.png', null, 'NEXUS N7000', 1, 1);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (16, 'SR', '', 'Описание Коньки BAUER NSX, любительская модель линейки NSX 2018 года. Коньки NSX разработаны специально для игроков начального уровня. При их создании особое внимание было уделено вопросу безопасности и комфорта езды. Внешняя часть ботинка покрыта вставками из специального пластика, который обеспечивает дополнительную защиту от порезов, статических и динамических нагрузок. Язык выполнен из войлока плотностью 30 унций с дополнительной защитой в области плюсневой кости. Особенности: • Уровень – Начальный • Внешний материал – Ударопрочный пластик • Внутренний материал – MICROFIBER • Язык – Войлок плотностью 30 унций с дополнительной защитой в области плюсневой кости • Стелька – EVA FORM • Тормоформуемость – НЕТ • Стакан – TUUK LIGHTSPEED PRO II • Лезвие – STAINLESS Характеристики Возрастная группа SR', '/root/images/Коньки/BAUER_NSX_SR.png', null, 'NSX', 1, 1);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (17, 'YTH', '', 'Описание Коньки BAUER SUPREME 1S Yth - детская модель, новинка в линейке SUPREME 2016. Ботинок коньков обладает комфортной, анатомической посадкой. После термоформовки ботинки имеют комфортную посадку. Накладки Total Edge Comfort, расположенные по верхней кромке ботинок, создают дополнительный комфорт, исключая натирание мягких тканей ноги при динамичном движении. Суппорт голеностопа, выполненный композитного материала Curv Composite, обеспечивает поддержку голеностопного сустава, повышает жесткость ботинка. Цельная конструкция системы шнуровки Stability Lacing System позволяет сильнее прижать стопу в нижней части ботинка, снизить его вес и увеличить прочность. Пена, обладающая эффектом памяти и используемая как внутреннее наполнение в области лодыжки, обеспечивает плотную фиксацию ноги внутри ботинка, что способствует более эффективной передачи энергии при движении по льду. Внутренний язычок надежно защищает переднюю часть голеностопа, имеет гибридное войлочное покрытие с плотностью 52 oz и 40 oz. Гидрофобный, сетчатый материал, используемый при изготовлении внутренней подкладки, за счет применения технологии 37.5 способствует быстрому испарению влаги, используя для этого тепло тела спортсмена. Композитная подошва с вентиляционными отверстиями гарантирует максимальную отдачу при каждом шаге. В коньках установлены стаканы Tuuk LightSpeed Pro. Коньки снабжены лезвиями из нержавеющей стали Tuuk LightSpeed LS1. Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME 1S_YTH.png', null, 'SUPREME 1S', 1, 1);
