INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (29, 'JR', '', 'Описание Коньки BAUER VAPOR X2.5, любительская модель линейки VAPOR 2019 года. Конек данной модели выполнен по технологии ONE PIECE 3D POLYCARB QUARTER, благодаря чему ботинок лучше сидит в области лодыжки, а также отличается небольшим весом, обладает повышенным удобством и гибкостью. Коньки оснащены войлочным язычком плотностью 30 унций с защитной вставкой из пены высокой плотности, в области плюсневой кости, которая помогает достичь максимального комфорта, а также хороших защитных свойств. Лезвия данной модели выполнены из высококачественной и прочной нержавеющей стали. Особенности: ✅Уровень – Любительский ✅Внешний материал – ONE PIECE 3D POLYCARB QUARTER ✅Внутренний материал – MICROFIBER ✅Язык – Войлок плотностью 30 унций ✅Стелька – EVA FOAM ✅Термоформуемость – Частичная формовка в области лодыжки ✅Подошва – TPR ✅Стакан – TUUK LIGHTSPEED PRO II ✅Лезвие – TUUK STAINLESS Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Коньки/BAUER_VAPOR X2.5_JR.png', null, 'VAPOR X2.5', 1, 1);
