INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (51, 'JR', '', 'Описание Коньки BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Основные особенности этого конька легкость и надежность. Благодаря композитному материалу 3-D LASTED CURV®, конек после формовки садиться максимально комфортно. Новый язычок REFLEX имеет трехсоставную конструкцию и защиту плюсневой кости. Войлочное покрытие языка 48 унций. Особенности: • Уровень – Профессиональный • Внешний материал – 3-D LASTED CURV® • Внутренний материал – LOCK-FIT • Язык – REFLEX • Стелька – LOCK-FIT • Термоформуемость – Полная • Подошва – Композит • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS3 (р-р 4-12) TUUK LS2 (р-р 1-3.5) Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Коньки/BAUER_SUPREME 2S_JR.png', null, 'SUPREME 2S', 1, 1);
