INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (85, 'JR', '', 'Описание Наплечник BAUER NEXUS 4000 начального уровня, имеет классическую, традиционную посадку и создает надежную защиту. Данная модель имеет однопанельную конструкцию как спереди, так и сзади. Сегментированное строение панели защиты груди и спины позволяет хоккеисту свободно двигаться. Панель защиты груди выполнена из пены средней плотности и имеет центральный щиток, защищающий грудину, выполненный из пены высокой плотности с дополнительной пластиковой вставкой. Элементы защиты ключиц и лопаток изготовлены с внутренним наполнением из пены средней плотности. Плечевые чаши имеют внешнее покрытие из формованного пластика, которое уменьшает силу удара. Внутреннее, амортизирующее наполнение в плечевой чаше из пены средней плотности поглощает энергию, возникающую при ударе, и минимизирует болевые ощущения. Литые, односоставные элементы защиты бицепса из пластика и легкого пеноматериала, максимально полно охватывают верхнюю часть руки, создавая эффективную защиту. Регулируемые ремни крепления позволяют подобрать плотность посадки защитных элементов бицепса. Панель защиты спины изготавливается с внутренним наполнением из пены средней плотности. Дополнительная, защитная вставка из пластика закрывает позвоночник, надежно защищая его от травм. Эластичные ремни фиксации позволяют регулировать плотность посадки защитного снаряжения. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Нагрудник/BAUER_NEXUS 4000_JR.png', null, 'NEXUS 4000', 1, 3);
