INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (94, 'YTH', '', 'Описание Наплечник CCM JETSPEED FT350, любителская модель линейки CCM 2018 года. Особенности: • Уровень – Любителский • Защита плеч – Пена низкой плотности • Защита груди – Формованные вставки PE • Передняя защита ребер – PE FOAM • Защита позвоночника – Формованные вставки PE • Защита бицепса – Формованные вставки PE Характеристики Возрастная группа YTH Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT350_YTH.png', null, 'JETSPEED FT350', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (95, 'YTH', '', 'Описание Наплечник BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Наплечник предназначен для детей начинающего уровня. Основные защитные элементы выполнены из пены средней плотности. В качестве внутренней подкладки используется материал HYDROPHOBIC, который поглощает влагу и способствует быстрому высыханию. Особенности: ✅Уровень – Любительский ✅Защита плеч – Пена средней плотности MD ✅Защита груди - Пена средней плотности MD ✅Передняя защита ребер – Пена средней плотности MD ✅Защита спины – Пена средней плотности MD ✅Защита позвоночника – Пена средней плотности MD ✅Защита бицепса – Пена средней плотностью MD ✅Подкладка – HYDROPHOBIC Характеристики Возрастная группа YTH', '/root/images/Нагрудник/BAUER_NSX_YTH.png', null, 'NSX', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (96, 'YTH', '', 'Описание Наплечник WARRIOR ALPHA DX YTH, детская модель линейки ALPHA 2019 года. Основные особенности: ✅ DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты ✅ PROTECTION – Многослойная пена обеспечивает легкий вес, защиту и подвижность ✅LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа YTH Линейка ALPHA', '/root/images/Нагрудник/WARRIOR_ALPHA DX_YTH.png', null, 'ALPHA DX', 3, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (97, 'JR', '', 'Описание Наплечник WARRIOR ALPHA DX 5, любительская модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • PROTECTION – Многослойная пена обеспечивает легкий вес, защиту и подвижность Характеристики Возрастная группа JR Линейка ALPHA', '/root/images/Нагрудник/WARRIOR_ALPHA DX5_JR.png', null, 'ALPHA DX5', 3, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (98, 'JR', '', 'Описание Наплечник BAUER VAPOR X800 LITE, полупрофессиональная модель линейки VAPOR 2018 года. Наплечник данной модели характеризуется прекрасными защитными свойствами, удобством и надежностью. Эта модель имеет сегментированную конструкцию, отличается небольшим весом. Особенности: • Уровень – Полупрофессиональный • Защита плеч – Формованная MD FOAM(пена средней плотности) • Защита груди – Формованная HD FOAM(пена высокой плотности) • Передняя защита ребер – Перфорированная пена с ребристой вставкой MD FOAM • Защита спины – Перфорированная пена • Защита позвоночника – HD FOAM(пена высокой плотности) • Защита ключицы – HD FOAM(пена высокой плотности); технология FREE FLEX • Подкладка –HYDROPHOBIC; технология SANITIZED® Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR X800 LITE_JR.png', null, 'VAPOR X800 LITE', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (99, 'JR', '', 'Описание Наплечник CCM TACKS 4052, новинка сезона 2015/16, имеет удобную посадку и отличается хорошей мобильностью за счет многопанельной конструкции. Нагрудник выполнен из легкой, формованной, перфорированной пены Foam Armour и имеет сегментированное строение, которое позволяет игроку свободно двигаться и с легкостью сгибаться. Вентиляционные отверстия в пене создают постоянный приток воздуха во внутреннее пространство нагрудника. Плавающий, центральный щиток, защищающий грудину, выполнен из пены высокой плотности и имеет дополнительную, сегментированную вставку из ударопрочного полиэтилена. Плечевые чаши выполнены из формованной пены высокой плотности и имеют литые пластиковые вкладки, что создает эффективную защиту плечевых суставов. Элементы защиты бицепса выполнены из формованного, ударопрочного полиэтилена. Модель имеет съемный, сегментированный щиток, защищающий живот. Задняя часть нагрудника имеет так же двух панельную конструкцию, где плавающий щиток с пластиковой вставкой закрывает и защищает позвоночник. Нагрудник комфортно сидит на теле и плотность его посадки можно подобрать с помощью эластичных ремней на липучке, расположенных на боках. Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 4052_JR.png', null, 'TACKS 4052', 2, 3);
