INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (101, 'YTH', '', 'Описание Наплечник CCM TACKS 9040 YTH, детская любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для детей начинающего уровня. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP с использованием пластика, чтобы защитить сустав от ударов. Все защитные элементы наплечника выполнены из комбинации пен разной плотности, что дает больше удобства. Между элементами защиты присутствуют эластичные вставки, которые дают ребенку больше свободы движения. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пена средней плотности ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пена средней плотности ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9040_YTH.png', null, 'TACKS 9040', 2, 3);
