INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (109, 'SR', '', 'Описание Наплечник CCM TACKS 4052, новинка сезона 2015/16, имеет удобную посадку и отличается хорошей мобильностью за счет многопанельной конструкции. Нагрудник выполнен из легкой, формованной, перфорированной пены Foam Armour и имеет сегментированное строение, которое позволяет игроку свободно двигаться и с легкостью сгибаться. Вентиляционные отверстия в пене создают постоянный приток воздуха во внутреннее пространство нагрудника. Плавающий, центральный щиток, защищающий грудину, выполнен из пены высокой плотности и имеет дополнительную, сегментированную вставку из ударопрочного полиэтилена. Плечевые чаши выполнены из формованной пены высокой плотности и имеют литые пластиковые вкладки, что создает эффективную защиту плечевых суставов. Элементы защиты бицепса выполнены из формованного, ударопрочного полиэтилена. Модель имеет съемный, сегментированный щиток, защищающий живот. Задняя часть нагрудника имеет так же двух панельную конструкцию, где плавающий щиток с пластиковой вставкой закрывает и защищает позвоночник. Нагрудник комфортно сидит на теле и плотность его посадки можно подобрать с помощью эластичных ремней на липучке, расположенных на боках. Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 4052_SR.png', null, 'TACKS 4052', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (110, 'SR', '', 'Описание Наплечник CCM JETSPEED FT350, любителская модель линейки CCM 2018 года. Особенности: • Уровень – Любителский • Защита плеч – Пена низкой плотности • Защита груди – Формованные вставки PE • Передняя защита ребер – PE FOAM • Защита позвоночника – Формованные вставки PE • Защита бицепса – Формованные вставки PE Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT350_SR.png', null, 'JETSPEED FT350', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (111, 'YTH', '', 'Описание Наплечник CCM SUPER TACKS AS1, детская профессиональная модель линейки TACKS 2019 года. Данный наплечник разработан специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Плечевые чаши наплечника выполнены из высококачественного полиэтилена, который обеспечивает максимальную защиту при минимальном весе. Для обеспечения высокого уровня защиты в области груди используется пена D3O, которая максимально поглощает энергию удара. Так же область груди имеет подвижную конструкцию, обеспечивая максимальный комфорт. Для защиты позвоночника используется пена высокой плотности. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – Высококачественный полиэтилен ✅Защита ключицы – Пена средней плотности ✅Защита груди – D3O ✅Защита позвоночника – Пена высокой плотности Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Нагрудник/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (112, 'JR', '', 'Описание Наплечник EASTON PRO 10, модель профессионального уровня имеет эргономичную конструкцию, которая обеспечивает подвижность и максимальную защиту. Передняя часть нагрудника имеет трехпанельную конструкцию. В качестве материала для внутреннего наполнения используется сочетание пен различной плотности с усиливающими пластиковыми вкладками, что создает максимальную защиту. Так центральные щитки, закрывающие грудину и позвоночник, выполнены из толстого слоя пены средней плотности с усиливающими вставками из вспененного полиэтилена. Элементы, закрывающие боковую часть груди, съемный элемент, закрывающий брюшную полость, имеют наполнение из легкой, вентилируемой пены двойной плотности с основой из пены E-Cell, которая имеет значительно меньший вес по отношению к обычной пене и активно участвует в вентиляции снаряжения. Область ключиц защищена расширенными элементами, выполненными из толстого слоя пены армированного вспененным полиэтиленом. Плечевые чаши выполнены из формованного, вспененного полиэтилена и выстланы вентилируемой пены двойной плотности. Такая конструкция позволяет поглотить силу удара и снизить болевые ощущения. Положение плечевых чаш можно регулировать с помощью липучки. Элементы защиты бицепса имеют двухсоставную конструкцию, которая регулируется по высоте с помощью липучки, что позволяет подобрать необходимую плотность посадки. Пена средней плотности с формованными пластиковыми вставками используется при изготовлении элементов защиты бицепса. Конструкция задней части нагрудника аналогична фронтальной. Сегментированная задняя часть, заполненная пеной средней плотности и пеной E-Cell, плотно охватывает спину игрока, соединяясь с фронтальной частью с помощью сегментированных элементов, закрывающих и защищающих нижнюю часть ребер и область почек. Регулирование нагрудника в нижней части происходит с помощью крепления на липучке. Ворот выполнен из мягкой пены и покрыт материалом, который контактирует с шеей игрока. Подкладка выполнена из быстро сохнущего материала. Характеристики Возрастная группа JR', '/root/images/Нагрудник/EASTON_PRO 10_JR.png', null, 'PRO 10', 12, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (113, 'YTH', '', 'Описание Наплечник BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди, для максимальной защиты, используется формованная пена средней плотности с вставкой из полиэтилена. Область бицепса имеет цельную конструкцию с защитной вставкой из формованного полиэтилена. Плечевые чаши выполнены из полиуретана. В качестве реберной защиты используется пена VENT ARMOR. В качестве внутренней подкладки используется материал THERMO MAX+, который эффективно отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – Формованный полиуретан ✅Защита груди – Формованная MD FOAM(пена средней плотности); полиэтилен ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – MD FOAM(пена средней плотности) ✅Защита позвоночника – MD FOAM(пена средней плотности); полиэтилен ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (114, 'JR', '', 'Описание Наплечник CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для игроков любителей. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP, которая рассеивает энергию удара. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пластик ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9040_JR.png', null, 'TACKS 9040', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (115, 'JR', '', 'Описание Наплечник WARRIOR ALPHA DX 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC PROTECTION – Формованная пластина в сочетании с пеной двойной плотности обеспечивает защиту в ключевых зонах • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа JR Линейка ALPHA', '/root/images/Нагрудник/WARRIOR_ALPHA DX3_JR.png', null, 'ALPHA DX3', 3, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (116, 'JR', '', '', '/root/images/Нагрудник/CCM_SUPER TACKS_JR.png', null, 'SUPER TACKS', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (117, 'JR', '', 'Описание Наплечник BAUER NEXUS 1N топовая модель в линейке Nexus 16 года. Традиционная посадка отлично сочетается с мультисегментной конструкцией нагрудника, гарантируя игроку высокий уровень подвижности защитного снаряжения и надежную защиту. Базовый слой нагрудника выполнен из пены средней плотности и разделен на сегменты для максимальной гибкости. Для создания дополнительной защиты ребер и области почек нагрудник усилен сегментами из пены высокой плотности. Подвижный щиток, закрывающий грудину, скомбинирован из пены высокой плотности, пластиковой вставки и формованной пены, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. В плечевых чашах, выполненных из литого пластика, так же используется пена с технологией Seven+, которая располагается в центре и обеспечивает эффективную защиту суставов. Съемная защита живота выполнена из пены средней плотности. Ворот нагрудника имеет мягкую окантовку из легкой пены, которая не травмирует кожу. Литой, двухсоставной, термоформируемый элемент защиты бицепса имеет свободную конструкцию Flex, которая обеспечивает максимальную мобильность этого элемента. Регулируемый ремень позволяет подобрать оптимальную длину элемента защиты бицепса. В задней части нагрудника используются перекрывающиеся сегменты из пены высокой плотности, которые проходят вдоль позвоночника, обеспечивая защиту профессионального уровня. Центральный щиток, закрывающий позвоночник, имеет широкую форму, перекрывая область сегментированной пены высокой плотности в базовом слое, что гарантирует высокий уровень защиты. Область ключиц и лопаток покрыта арочными элементами Free Glide Arch, при изготовлении которых используется пена высокой плотности. Арочные элементы движутся отдельно от базового слоя, обеспечивая игроку полную свободу движений. Внутренняя подкладка, выполненная по технологии 37.5, использует тепло тела хоккеиста для ускорения процесса испарения влаги и создания комфортного для игрока микроклимата. Специальная обработка материала подкладки исключает появление неприятных запахов. Эластичные ремни крепления с фиксацией на липучке позволяют закрепить нагрудник на талии и элементы защиты бицепса на руках игрока. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Нагрудник/BAUER_NEXUS 1N_JR.png', null, 'NEXUS 1N', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (118, 'SR', '', 'Описание Наплечник CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данный наплечник предназначен для игроков любителей. Для защиты груди и спины используется пластик. Плечевые чаши выполнены по технологии JDP, которая рассеивает энергию удара. Особенности: ✅Уровень – Любительский ✅Защита плеч – JDP ✅Защита груди – Пластик ✅Передняя защита ребер – Пена низкой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9040_SR.png', null, 'TACKS 9040', 2, 3);
