INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (120, 'SR', '', 'Описание Наплечник CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Особенности: • Уровень – Полупрофессиональный • Защита плеч – Пена низкой плотности • Защита ключицы – Пластик • Защита груди – Формованные вставки PE • Передняя защита ребер – PE FOAM • Защита позвоночника – Формованные вставки PE • Защита бицепса – PE FOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (121, 'JR', '', 'Описание Наплечник BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена по сравнению с предыдущей коллекция BAUER SUPREME S170, теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – Формованная MD FOAM(пена средней плотности) ✅Защита груди – Формованная HD FOAM(пена высокой плотности) ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (122, 'JR', '', 'Описание Наплечник BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Наплечник обладает отличной мобильностью и его вес снижен примерно на 25% по сравнению с предыдущей моделью за счет использования в его конструкции облегченных материалов. Новейшая пена AEROLITE 2.0 имеет очень легкий вес и при этом не теряет своих защитных функций. Очень легкий композитный материал CURV® усиливает защитные свойства в стратегически важных местах. Внутренний материал выполнен с применением технологии 37,5®. Во время физических нагрузок ускоряется процесс испарения излишков влаги, что способствует быстрому высыханию наплечника. Помимо этого материал подкладки обрабатывается по технологии SANITIZED®, которая препятствует возникновению неприятных запахов в процессе игры. Особенности: • Уровень – Элитный • Защита плеч – AEROLITE 2.0 • Защита груди- CURV®; плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – CURV®; плавающая конструкция • Защита ключицы – HD FOAM; технология FREE FLEX • Защита бицепса – CURV®; технология FREE FLEX • Подкладка – Технология 37,5®; технология SANITIZED® Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR 1X LITE_JR.png', null, 'VAPOR 1X LITE', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (123, 'SR', '', 'Описание Наплечник BAUER SUPREME S17 S190 Защита Supreme спроектирована, чтобы обеспечить идеальную анатомическую посадку, которая даст максимальную энергию в каждом игровом эпизоде! Особенности: • Съемная защита живота • Защита груди: XRD и пена высокой плотности • Защита спины: пена высокой плотности со вставками из полиэтилена • Защита бицепса: пена средней плотности со вставками из полиэтилена • Термоформованые чашки с пеной средней плотности • Подкладка: THERMO MAX Нагрудник Supreme S190 предлагает профессиональный уровень защиты благодаря интегрированными накладками на плечах, формованному пенному наполнителю Vent Armor в области груди и пенным наполнителям средней плотности, защищающим грудь, живот и поясницу. В области спины используется пена высокой плотности с вставками из полиэтилена для обеспечения защиты в одном из наиболее уязвимых мест. Подкладка THERMO MAX испаряет пот с вашего тела, сохраняя прохладу и сухость во время игры. Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME S190_SR.png', null, 'SUPREME S190', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (124, 'SR', '', 'Описание Наплечник BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Наплечник обладает высоким уровень защиты и при этом имеет легкий вес. Ключевым изменением по сравнению с прошлой моделью стало внедрение плавающей конструкции защиты позвоночника, а также использование перфорированной пены со стороны спины. Особенности: • Уровень – Профессиональный • Защита плеч – Пена HYPERLITE • Защита груди – Пена HYPERLITE, плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – Пена HYPERLITE; плавающая конструкция • Защита ключицы – HD FOAM(пена высокой плотности); технология FREE FLEX • Защита бицепса – Пена HYPERLITE; технология FREE FLEX • Подкладка – TERMO MAX; технология SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR X900 LITE_SR.png', null, 'VAPOR X900 LITE', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (125, 'JR', '', 'Описание Наплечник CCM SUPER TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Для максимальной защиты груди и позвоночника используется пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – JDP ✅Защита ключицы – Пена средней плотности ✅Защита груди – Пластик ✅Передняя защита ребер – Пена средней плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена средней плотности Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9060_JR.png', null, 'TACKS 9060', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (126, 'SR', '', 'Описание Наплечник CCM ULTRA TACKS, верхняя модель в линейке CCM Tacks обладает легкой конструкцией и предлагает профессиональную защиту и мобильность. Передняя и задняя части нагрудника имеют двухпанельную конструкцию и сегментированное строение и улучшенную систему воздухопроницаемости, что положительным образом сказывается на комфорте. В качестве внутреннего наполнения в передней панели и в плавающем центральном щитке, защищающем грудину, используется пена высокой плотности. Плечевые чаши выполнены из формованного полиэтилена, имеют набивку из пены высокой плотности Dual Core. Кроме того в плечевых чашах используется специализированная пена D3Oоранжевого цвета. Этот защитный материал по-разному реагирует на силу удара. Пена остается мягкой и подвижной в обычном состоянии и при поглощении несильных ударов, в то время как при ударах высокой силы пена моментально твердеет, создавая эффективную защиту плечевого сустава, после чего снова возвращается в мягкое, гибкое состояние. Элементы защиты бицепса имеют двухсоставную конструкцию и выполнены из литой пены высокой плотности. Элемент защиты бицепса имеет съемную полиэтиленовую вкладку, создающую усиленную защиту. В зависимости от необходимого уровня защиты, игроки могут настроить посадку элементов защиты живота и бицепса или удалить их совсем. Задняя часть нагрудника имеет плавающую панель, закрывающую позвоночник и выполненную из пены высокой плотности. Усиленные элементы обеспечивают охватывающую защиту почек и нижних ребер. В качестве подкладки используется дополнительный слой армированной пены и гидрофобная, износоустойчивая микрофибра. Новая, улучшенная система связей Anti-lift System между элементами защиты позволяет им двигаться более свободно вместе с телом игрока. Смещение одного элемента не будет вызывать смещение соседнего элемента, например, при поднятии рук вверх защитная жилетка не будет смещаться. Для фиксации нагрудника используются вентилируемые, эластичные ремни на липучках, крепящиеся на талии. Упругие ремешки на липучках фиксируют элементы защиты бицепса. Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Нагрудник/CCM_ULTRA TACKS_SR.png', null, 'ULTRA TACKS', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (127, 'JR', '', 'Описание Наплечник CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости и мобильности. Защита плеч выполнена из пены EVA, которая имеет очень легкий вес и хорошие защитные свойства. Защита груди и позвоночника имеет анатомические, формованные вставки PE SHELLS. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита плеч – EVA FOAM • Защита ключицы – Пластик; плавающая конструкция • Защита груди – PE SHELLS • Передняя защита ребер – Пена высокой плотности • Защита позвоночника – PE SHELLS • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (128, 'SR', '', '', '/root/images/Нагрудник/CCM_SUPER TACKS_SR.png', null, 'SUPER TACKS', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (129, 'SR', '', 'Описание Наплечник BAUER NEXUS 1N топовая модель в линейке Nexus 16 года. Традиционная посадка отлично сочетается с мультисегментной конструкцией нагрудника, гарантируя игроку высокий уровень подвижности защитного снаряжения и надежную защиту. Базовый слой нагрудника выполнен из пены средней плотности и разделен на сегменты для максимальной гибкости. Для создания дополнительной защиты ребер и области почек нагрудник усилен сегментами из пены высокой плотности. Подвижный щиток, закрывающий грудину, скомбинирован из пены высокой плотности, пластиковой вставки и формованной пены, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. В плечевых чашах, выполненных из литого пластика, так же используется пена с технологией Seven+, которая располагается в центре и обеспечивает эффективную защиту суставов. Съемная защита живота выполнена из пены средней плотности. Ворот нагрудника имеет мягкую окантовку из легкой пены, которая не травмирует кожу. Литой, двухсоставной, термоформируемый элемент защиты бицепса имеет свободную конструкцию Flex, которая обеспечивает максимальную мобильность этого элемента. Регулируемый ремень позволяет подобрать оптимальную длину элемента защиты бицепса. В задней части нагрудника используются перекрывающиеся сегменты из пены высокой плотности, которые проходят вдоль позвоночника, обеспечивая защиту профессионального уровня. Центральный щиток, закрывающий позвоночник, имеет широкую форму, перекрывая область сегментированной пены высокой плотности в базовом слое, что гарантирует высокий уровень защиты. Область ключиц и лопаток покрыта арочными элементами Free Glide Arch, при изготовлении которых используется пена высокой плотности. Арочные элементы движутся отдельно от базового слоя, обеспечивая игроку полную свободу движений. Внутренняя подкладка, выполненная по технологии 37.5, использует тепло тела хоккеиста для ускорения процесса испарения влаги и создания комфортного для игрока микроклимата. Специальная обработка материала подкладки исключает появление неприятных запахов. Эластичные ремни крепления с фиксацией на липучке позволяют закрепить нагрудник на талии и элементы защиты бицепса на руках игрока. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Нагрудник/BAUER_NEXUS 1N_SR.png', null, 'NEXUS 1N', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (130, 'SR', '', 'Описание Наплечник BAUER SUPREME S17 1S Весной 2017 года Bauer обновил всю модельную линейку защиты Supreme, которая теперь доступна для заказа в России! Нагрудник Supreme 1S гарантирует идеальную анатомическую посадку, что обеспечит вас максимальной энергией в каждом игровом эпизоде. Особенности: • Чашечка: Powerlite • Торс: Vent Armor • Солнечное сплетение: Curv • Подкладка: 37.5 + sanitized В нагруднике Supreme 1S применены композитные вставки из материала CURV с иcпользованием технологии Powerlite. Композитный материал CURV уже знаком по топовым моделям коньков, но технология Powerlite используется впервые. Powerlite это легкая и формуемая пена, обеспечивающая эффект облегания и, как итог, анатомическую посадку нагрудника. В области груди используется дополнительная защита из композитного материала Curv в сочетании с FleXorb. FleXorb задействует пену XRD для непревзойденного поглощения энергии удара. Подкладка 37,5 обеспечивает быстрое высыхание, хорошую вентиляцию и предотвращает размножение бактерий и неприятный запах. Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME 1S_SR.png', null, 'SUPREME 1S', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (131, 'SR', '', 'Описание Наплечник BAUER VAPOR 1X флагманская модель в линейке Vapor, новинка 2016 года, имеет сверхлегкую конструкцию, обладает максимальной подвижностью и высоким уровнем защиты. Нагрудник имеет сегментированную, мультипанельную конструкцию Free Flex. Эластичные соединения между панелями позволяют элементам защиты находиться на тех местах, где они должны быть при активном движении игрока и не смещаться. Центральный щиток на передней панели, закрывающий грудину, выполнен из композитного материала Curv, который обладает высокой прочностью, легкостью, создавая невесомую защиту. Композитный материал дополнен легкой пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами. Область ключиц защищена арочными элементами, выполненными из пены высокой плотности, которые движутся за счет эластичных соединений вместе с рукой, создавая надежную защиту. Базовый слой в боковых панелях так же выполнен из пены Aerolite. Съемный элемент защиты живота выполнен из пены средней плотности. Задняя часть нагрудника так же имеет подвижное, сегментированное строение, где центральная часть, закрывающая позвоночник, выполнена из пены высокой плотности. Область почек защищена пеной Aerolite. Регулируемые, двухсоставные элементы защиты бицепса скомбинированы из композитного материала Curv и легкой пены Aerolite. Плечевые чаши сформованы из легкой пены Aerolite, которая эффективно поглощает силу удара, надежно защищая хрупкие суставы. Гидрофобный, сетчатый материал с технологией 37.5 используется в качестве внутренней подкладки. Данный материал позволяет использовать тепло тела для быстрого испарения влаги, обеспечивая быстрый отвод тепла и влаги от тела игрока и создавая исключительный уровень комфорта. Окантовка горловины из мягкой пены обеспечивает комфортное прилегание защитного снаряжения к шее. Эластичные ремни крепления на липучке фиксируют нагрудник на талии и элементы защиты на бицепсе. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR 1X_SR.png', null, 'VAPOR 1X', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (132, 'JR', '', 'Описание Наплечник BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения, а пена IX-FOAM, которая использована в них служит для максимального поглощения энергии удара. Для максимальной подвижности и комфорта в области бицепса используется конструкция AMP. В качестве внутренней подкладки используется материал THERMO MAX+, который максимально хорошо отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – IX-FOAM ✅Защита груди – Плавающая конструкция из пены высокой плотности ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME 2S_JR.png', null, 'SUPREME 2S', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (133, 'SR', '', 'Описание Наплечник CCM SUPER TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Для максимальной защиты груди и позвоночника используется пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – JDP ✅Защита ключицы – Пена средней плотности ✅Защита груди – Пластик ✅Передняя защита ребер – Пена средней плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена средней плотности Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9060_SR.png', null, 'TACKS 9060', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (134, 'SR', '', 'Описание Наплечник BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена по сравнению с предыдущей коллекция BAUER SUPREME S170, теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – Формованная MD FOAM(пена средней плотности) ✅Защита груди – Формованная HD FOAM(пена высокой плотности) ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME S29_SR.png', null, 'SUPREME S29', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (135, 'SR', '', 'Описание Наплечник BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Наплечник 2N создан, чтобы защищать. За счет полного покрытия тела, наплечник считается самым защищенным из BAUER. В плечевых чашах используется материал IX FOAM, что дает вам безопасность во время силовых приемах. Защита груди и позвоночника сегментированное строение из пены MDP. В защите бицепса в ключевых зонах воздействия используется вставки из полиэтилена, а внутренний материал THERMO MAX с технологией SANITIZED поддерживает комфортную температуру и предотвращает образование бактерий и появление неприятного запаха. Особенности: • Уровень – Элитный • Защита плеч – IX FOAM • Защита груди- MDP;плавающая конструкция • Передняя защита ребер – Перфорированная пена; VENT ARMOR • Защита спины – Перфорированная пена; VENT ARMOR • Защита позвоночника – MDP;плавающая конструкция • Защита ключицы – HD FOAM; технология FREE FLEX • Защита бицепса – Полиэтилен PE; технология FREE FLEX • Подкладка – THERMO MAX; технология SANITIZED® Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Нагрудник/BAUER_NEXUS 2N_SR.png', null, 'NEXUS 2N', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (136, 'SR', '', 'Описание Наплечник WARRIOR ALPHA DX PRO , профессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC CAP – Защищает плечи от повреждений при различных столкновения и ударах, в то же время снижает риск травмы для другого участника столкновения • DX SHELL- Пена EVA ,спрессованная по технологии SHELL SKIN, добавлена в стратегически важных местах, чтобы обеспечить дополнительный уровень защиты там, где это необходимо больше всего • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • WARTECH FNC LINER – Антибактериальная подкладка обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Нагрудник/WARRIOR_ALPHA DX PRO_SR.png', null, 'ALPHA DX PRO', 3, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (137, 'SR', '', 'Описание Наплечник BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Наплечник обладает отличной мобильностью и его вес снижен примерно на 25% по сравнению с предыдущей моделью за счет использования в его конструкции облегченных материалов. Новейшая пена AEROLITE 2.0 имеет очень легкий вес и при этом не теряет своих защитных функций. Очень легкий композитный материал CURV® усиливает защитные свойства в стратегически важных местах. Внутренний материал выполнен с применением технологии 37,5®. Во время физических нагрузок ускоряется процесс испарения излишков влаги, что способствует быстрому высыханию наплечника. Помимо этого материал подкладки обрабатывается по технологии SANITIZED®, которая препятствует возникновению неприятных запахов в процессе игры. Особенности: • Уровень – Элитный • Защита плеч – AEROLITE 2.0 • Защита груди- CURV®; плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – CURV®; плавающая конструкция • Защита ключицы – HD FOAM; технология FREE FLEX • Защита бицепса – CURV®; технология FREE FLEX • Подкладка – Технология 37,5®; технология SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Нагрудник/BAUER_VAPOR 1X LITE_SR.png', null, 'VAPOR 1X LITE', 1, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (138, 'JR', '', 'Описание Наплечник CCM SUPER TACKS 9080, профессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Новая система регулировки позволяет отрегулировать наплечник по объему. Для защиты груди используется пена D30 LITE, которая поглощает энергию удара и формованный полиэтилен. В области позвоночника для максимальной защиты используется пена средней плотности и пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара, с использованием пены D3O LITE. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – JDP; D3O LITE ✅Защита ключицы – Пена высокой плотности ✅Защита груди – D3O LITE; формованный полиэтилен ✅Передняя защита ребер – Пена высокой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена высокой плотности Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Нагрудник/CCM_TACKS 9080_JR.png', null, 'TACKS 9080', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (139, 'SR', '', 'Описание Наплечник CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости и мобильности. Защита плеч выполнена из пены EVA, которая имеет очень легкий вес и хорошие защитные свойства. Защита груди и позвоночника имеет анатомические, формованные вставки PE SHELLS. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита плеч – EVA FOAM • Защита ключицы – Пластик; плавающая конструкция • Защита груди – PE SHELLS • Передняя защита ребер – Пена высокой плотности • Защита позвоночника – PE SHELLS • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT390_SR.png', null, 'JETSPEED FT390', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (140, 'JR', '', 'Описание Наплечник CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости, мобильности и технологичности. Одна из главных технологий, которая будет использоваться в наплечники данной модели это очень легкий и жесткий композитный материал ROCKETFRAME. Защита плеч выполнена из пены высокой плотности U-FOAM. Новая конструкция внутренней части повторяет движения тела. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Элитный • Защита плеч – Конструкция UFOAM • Защита ключицы – Пена высокой плотности; плавающая конструкция • Защита груди – ROCKETFRAME • Передняя защита ребер - PREMIER ZOTEFOAM® • Защита позвоночника – ROCKETFRAME • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Нагрудник/CCM_JETSPEED FT1_JR.png', null, 'JETSPEED FT1', 2, 3);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (141, 'JR', '', 'Описание Наплечник BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди для максимальной защиты используется композитный материал CURV, который придает наплечнику более легкий вес и максимальную защиту. В качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения, а пена PORON XRD, которая использована в них, служит для максимального поглощения энергии удара. Для максимальной подвижности и комфорта в области бицепса и ребер используется конструкция AMP. В качестве внутренней подкладки используется материал THERMO CORE, который максимально хорошо отводит влагу. Особенности: ✅Уровень – Элитный ✅Защита плеч – PORON XRD ✅Защита груди – CURV ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – VENT ARMOR ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO CORE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Нагрудник/BAUER_SUPREME 2S PRO_JR.png', null, 'SUPREME 2S PRO', 1, 3);
