INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (155, 'SR', '', 'Описание Наколенники BAUER VAPOR X800 LITE, полупрофессиональная модель линейки VAPOR 2018 года. Наколенники Х800 LITE имеют клиновидную посадку. Благодаря технологии FREE FLEX щитки очень удобны и подвижны. Хороший уровень защиты обеспечивает пена высокой плотности HYPERLITE. Особенности: • Уровень – Полупрофессиональный • Посадка - Клиновидная • Защита икры – Пена HYPERLITE • Боковая защита – MD FOAM(пена средней плотности) • Защита бедра – MD FOAM(пена средней плотности) • Крепление – Два ремня фиксируются сверху и снизу • Подкладка – HYDROPHOBIC; SANITIZED®. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Щитки/BAUER_VAPOR X800 LITE_SR.png', null, 'VAPOR X800 LITE', 1, 4);
