INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (157, 'JR', '', 'Описание Наколенники BAUER NSX, любительская модель линейки NSX Основные защитные элементы созданы из ударопрочного пластика. Внутренняя подкладка выполнена из гидрофобной сетки для лучшего отвода влаги. Особенности: • Уровень – Любительский • Посадка - Клиновидная • Защита голени – Ударопрочный пластик • Защита икры – Пена средней плотности; ударопрочный пластик • Защита бедра – Пена • Крепление – Два ремня фиксируются сверху и в области икры • Подкладка - HYDROPHOBIC Характеристики Возрастная группа JR', '/root/images/Щитки/BAUER_NSX_JR.png', null, 'NSX', 1, 4);
