INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (160, 'YTH', '', 'Описание Наколенники CCM SUPER TACKS AS1 YTH, детская профессиональная модель линейки TACKS 2019 года. Данные наколенники разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Область колена и голени выполнена из формованного полиэтилена высокого качества, благодаря этому наколенники имеют легкий вес и хорошие защитные функции. Для максимального удобства и комфорта используется два ремня регулировки. Съемный внутренний вкладыш с пеной D3O в области коленной чашки, максимально защищает от прямых ударов и остается сухим во время игры. Особенности: ✅Уровень – Профессиональный ✅Защита колена – D3O ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Щитки/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (161, 'YTH', '', 'Описание Наколенники BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет анатомическую посадку. Для защиты колена и бедра используется пена средней плотности. Область икры выполнена из пены VENT ARMOR, которая придает наколеннику более легкий вес и хорошо поглощает энергию удара. Для максимального удобства, в качестве регулировки, используется мягкая эластичная липучка. Внутренняя подкладка выполнена по технологии THERMO MAX+, благодаря этому наколенник быстро сохнет. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита икры – VENT ARMOR ✅Боковая защита – Пена средней плотности ✅Защита колена – Пена средней плотности ✅Защита бедра – Пена средней плотности ✅Крепление – 1 эластичная широкая липучка ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 4);
