INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (169, 'JR', '', 'Описание Наколенники BAUER SUPREME S17 1S Скользите быстрее и эффективнее с новыми щитками Supreme 1S! В щитках Supreme 1S впервые применен композитный материал Curv®, который в сочетании с профилированной пеной POWERLITE, обеспечивает максимальное прилегание к телу, сохраняя при этом исключительный уровень защиты и минимальный вес. Особенности: • Общая посадка : Анатомическая форма • Конструкция: Многосегментный дизайн • Система крепления: 2 ремня с технологией Anchor Strap • Подкладка: 37,5® Technology и Sanitized® • Щиток на голень: Curv®и POWERLITE • Наколенник: Интегрированный наколенник • Внутренняя часть колена: FleXorb, G-FORM® и XRD®Material • Защита икр: профилированная пена средней плотности Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME 1S_JR.png', null, 'SUPREME 1S', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (170, 'JR', '', 'Описание Наколенники Bauer Vapor 1X – это верхняя профессиональная модель линейки Vapor 2016 года. Особенности: • Конусная посадка щитков Bauer Vapor 1X, широкая вверху и сужающаяся к голени, обеспечивает необходимую скорость и маневренность игрока. • Соответствуя высшему уровню стандартов по безопасности и защите, щитки Bauer Vapor 1X позволяют игроку быть максимально гибким и мобильным. • Баланс профессиональной защиты от ударов и минимального веса щитка достигается путем сочетания технологии Free Flex Curv и Aerolite • Применение гибких сегментов Free Flex в щитке обеспечивает игроку большую свободу движений и удобство. • Встроенная в подкладку инновационная технология 37.5 удаляет влагу, испаряя пот и делая щитки легче. Экипировка остается легкой на протяжении всей игры и поддерживает оптимальную температуру тела. Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Щитки/BAUER_VAPOR 1X_JR.png', null, 'VAPOR 1X', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (171, 'JR', '', 'Описание Наколенники CCM TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. В области икроножной мышцы для больше защиты используется пена средней плотности и пластик. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Полупрофессиональный ✅Защита икры – Пена средней плотности; пластик ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Щитки/CCM_TACKS 9060_JR.png', null, 'TACKS 9060', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (172, 'SR', '', 'Описание Наколенники WARRIOR ALPHA DX3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • PREMIUM STRAPS - Регулируемые ремни обеспечивают надежную фиксацию и удобство • LINER – Подкладка обеспечивает сухость Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Щитки/WARRIOR_ALPHA DX3_SR.png', null, 'ALPHA DX3', 3, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (173, 'JR', '', 'Описание Наколенники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры для большей защиты используется формованный полиэтилен. Для защиты бедра и колена используется пена средней плотности. Внутренняя подкладка THERMO MAX, которая отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита икры – Полиэтилен ✅Боковая защита – MD FOAM(пена средней плотности) ✅Защита бедра – MD FOAM(пена средней плотности) ✅Крепление – Два ремня фиксируются сверху и снизу Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (174, 'SR', '', 'Описание Наколенники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. В области икроножной мышцы использована формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется влагоотталкивающий материал. Особенности: • Уровень – Полупрофессиональный • Защита икры – Формованная пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка – PE FOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Щитки/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 4);
