INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (179, 'JR', '', 'Описание Наколенники BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры для большей защиты используется формованный полиэтилен. Для защиты бедра используется пена средней плотности, а в области коленной чашечки имеется пена IX-FOAM, которая поглощает энергию удара. Внутренняя подкладка THERMO MAX+, которая эффективно отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита икры – Полиэтилен ✅Боковая защита – VENT ARMOR ✅Защита колена – IX-FOAM ✅Защита бедра – MD FOAM(пена средней плотности) ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME 2S_JR.png', null, 'SUPREME 2S', 1, 4);
