INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (183, 'JR', '', 'Описание Наколенники CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Одним из главных преимуществ разработчиков стало то, что щитки имеют легкий вес и очень хорошие защитные функции. Главное отличие этих щитков от FT1 в том, что в области икры вместо композитного материала ROCKETFRAME, используется формованная пена высокой плотности с пластиковыми вставками. В защите бедра используется формованная пена высокой плотности. Так же одним из главных достижений разработчиков стала система регулировки наколенника. В данной модели можно отрегулировать длину липучки под себя, для максимального удобства. В качестве внутренней подкладки используется технология DRYFORM. Особенности: • Уровень – Профессиональный • Защита икры – Пластик; Пена высокой плотности • Защита бедра – PE FOAM • Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине • Подкладка - DRYFORM Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Щитки/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (184, 'JR', '', 'Описание Наколенники CCM TACKS 9080, профессиональная модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку, для лучшей защиты в стратегически важных местах используется пена D3O LITE, которая поглощает энергию удара. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Профессиональный ✅Защита икры – D3O LITE; пена высокой плотности ✅Защита бедра – Пена высокой плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Щитки/CCM_TACKS 9080_JR.png', null, 'TACKS 9080', 2, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (185, 'SR', '', 'Описание Наколенники WARRIOR ALPHA DX, топовая модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • SLING WRAP - Регулируемые ремни обеспечивают надежную фиксацию и максимальное удобство • DX SHELL – Пена EVA, спрессованная по технологии SHELL SKIN, добавлена в ключевые зоны, чтобы обеспечить дополнительный уровень защиты там, где это необходимо больше всего • WARTECH FNC LINER – Антибактериальная подкладка обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Щитки/WARRIOR_ALPHA DX_SR.png', null, 'ALPHA DX', 3, 4);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (186, 'SR', '', 'Описание Наколенники BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Наколенники имеют классическую посадку. Для защиты коленной чашечки используется пена IX FOAM, которая имеет легкий вес и хорошие защитные свойства. Икра защищена сегментированной пеной средней плотностью MDP. В боковой защите наколенника использована пена высокой плотностью HYPERLITE HD. Внутренняя подкладка THERMO MAX обработана по технологии SANITIZED, которая предотвращает развитие бактерий и возникновение неприятного запаха. Особенности: • Уровень – Элитный • Посадка – Классическая, широкая • Защита икры – Пена MDP • Боковая защита – Пена HYPERLITE • Защита бедра – Пена средней плотности • Крепление – Фиксируются сверху и Y-образный в области икры • Подкладка – THERMO MAX; SANITIZED®. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Щитки/BAUER_NEXUS 2N_SR.png', null, 'NEXUS 2N', 1, 4);
