INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (192, 'JR', '', 'Описание Наколенники BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры используется композитный материал CURV, который обеспечивает более легкий вес и максимальную защиту. Для защиты бедра используется пена высокой плотности, а в области коленной чашечки имеется пена PORON XRD, которая максимально поглощает энергию удара. Внутренняя подкладка THERMO CORE, которая эффективно отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Элитный ✅Посадка - Анатомическая ✅Защита икры – CURV ✅Боковая защита – VENT ARMOR ✅Защита колена – PORON XRD ✅Защита бедра – Пена высокой плотности ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка – THERMO CORE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Щитки/BAUER_SUPREME 2S PRO_JR.png', null, 'SUPREME 2S PRO', 1, 4);
