INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (205, 'YTH', '', 'Описание Шлем BAUER RE-AKT 100 YTH детская модель отличается легкой конструкцией и обеспечивает высокий уровень защиты. Ударопрочный корпус состоит из двух частей, смещающихся относительно друг друга, и имеет возможность регулировки плотности посадки без использования дополнительного инструмента за счет регулируемых затылочных упоров Spring-loaded Occipital Lock. Приток воздуха к голове и эффективный отвод тепла обеспечивают вентиляционные отверстия, имеющиеся в корпусе шлема. Внутреннее амортизирующее наполнение выполнено из пены тройной плотности. Специальная система защиты Suspend-Tech позволяет уменьшить вращательную силу при ударах по касательной, гарантируя эффективную защиту. Плавающая накладка, выполненная из пены Poron XRD, при ударе остается неподвижной, в то время как корпус шлема с амортизирующим наполнением смещается относительно головы на небольшой угол, рассеивая энергию удара. Прозрачный элемент, изготовленный из пластика и имеющий прорезиненное покрытие, обеспечивает надежную защиту ушной раковины. Возникновение неприятных запахов в процессе использования шлема предотвращает использование антибактериальной пропитки Microban. В области лба расположена дополнительная, влагопоглощающая прокладка, которая исключает попадание влаги в глаза. Ремешок под подбородком разработан специально для младших игроков и имеет возможность быстро затягиваться, фиксируя шлем на голове игрока. На шлем можно установить маску. Данная модель сертифицирована HECC, CSA и отвечает требованиям безопасности стандарта CE. Маска на шлем полевого игрока BAUER RE-AKT SR обеспечивает надежную защиту, полностью закрывая лицо. Маска изготовлена из ударопрочного титанового сплава, что делает ее на 46% легче традиционных защитных масок. Маска обеспечивает отличную видимость, за счет прямоугольной формы, овального сечения прутьев и оптимально подобранного расстояния между прутьями. Специально подобранный цвет маски не отражает свет прожекторов, делает черную шайбу более видимой, не отвлекает внимание игрока, позволяя полностью сконцентрироваться на движении шайбы. Плавающая, предварительно сформованная чаша для подбородка, выполнена из вспененного материала Poron XRD, обеспечивающего отвод влаги и отлично амортизирующего удары. Маска совместима с большинством сертифицированных хоккейных шлемов, подходит для всех шлемов Bauer. Маска прошла сертификацию CSA, HECC и имеет европейский знак соответствия требованиям безопасности CE. Характеристики Возрастная группа YTH', '/root/images/Шлем/BAUER_REAKT 100 YOUTH COMBO_YTH.png', null, 'REAKT 100 YOUTH COMBO', 1, 5);
