INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (209, 'UNDEFINED', '', 'Описание Шлем CCM TACKS 110, профессиональная модель линейки CCM . Данная модель имеет ударопрочный корпус, который создает оптимальный уровень защиты. Внутренняя подкладка выполнена из пены двойной плотности и отличается легким весом и отличными амортизирующими свойствами, которые эффективно гасят силу удара. Особенности: • Уровень – Профессиональный • Регулировка - Центральная, без использования инструментов • Ключевые технологии – Пена средней плотности • Сертифицирован – CSA, HECC и европейский знак соответствия требованиям безопасности CE Маска CCM FM580 Маска выполнена из углеродистой стали и имеет прямоугольное сечение, при этом производитель увеличил размер ячейки по сравнению с предыдущей моделью, благодаря чему улучшилась видимость. Подбородник выполнен из пены двойной плотности. Характеристики Линейка TACKS', '/root/images/Шлем/CCM_TACKS 110 COMBO_UNDEFINED.png', null, 'TACKS 110 COMBO', 2, 5);
