INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (231, 'YTH', '', 'Описание Налокотники BAUER SUPREME 150 YTH Посадка: • Плечевой ремешок - Застежка на липучке • Основной корпус - Многосегментный дизайн • Подкладка - Гидрофобный сетчатый материал • Общая посадка - Анатомическая форма Защита: • Дуга - Пена средней плотности • Щитки на спине для защиты почек - Пена средней плотности • Щиток для защиты живота - Съемная пена средней плотности • Защита бицепса - Прессованный термоформованный компонент • Фронтальные сегменты - Пена средней плотности • Защита плеч - Термоформованный щиток с пенополиуретаном • Защита грудной клетки - Вставки из пены высокой плотности + вставки из полиэтиленового материала Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 150_YTH.png', null, 'SUPREME 150', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (232, 'YTH', '', 'Описание Налокотники BAUER SUPREME S17 S170 YTH Играйте в своем стиле с анатомическими налокотниками Supreme S170. Двигайтесь максимально естественно и будьте уверены в своей защите! Особенности: • Общая посадка : Анатомическая форма • Подкладка: влаговпитывающая сетка Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME S170_YTH.png', null, 'SUPREME S170', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (233, 'YTH', '', 'Описание Налокотники EASTON SYNERGY GX SOFT YTH детская модель разрабатывалась специально для детей с учетом особенностей строения детского организма и с использованием фирменных технологий, адаптированных для детской хоккейной амуниции. Налокотники имеют односоставную конструкцию, при этом обладают хорошей мобильностью, удобной и плотной посадкой и отличаются хорошими защитными качествами. Элемент защиты бицепса выполнен из пены средней плотности, дополнительные вставки из облегченной, модернизированной пены E-Cell HD способствуют усилению защитных функций, снижению веса налокотников и обеспечению хорошей воздухопроницаемости защиты. Элемент защиты предплечья выполнен из пены средней плотности и эффективно защищает руки игрока от ударов по касательной. Подкладка, выполненная из эластичного материала, способствует быстрому поглощению влаги и созданию хорошей циркуляции воздуха внутри, а так же обеспечивает эффективную терморегуляцию, отводя излишки тепла от тела. Анатомическая посадка и надежная фиксация налокотников с помощью двух ремешков крепления исключает их смещение при активном движении. Характеристики Возрастная группа YTH Линейка SYNERGY', '/root/images/Налокотники/EASTON_SYNERGY GX SOFT_YTH.png', null, 'SYNERGY GX SOFT', 12, 7);
