INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (246, 'YTH', '', 'Описание Налокотники CCM SUPER TACKS AS1 YTH, детская профессиональная модель линейки CCM 2019 года. Данные налокотники разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O, которая поглощает энергию. Область бицепса выполнена из пены низкой плотности, которая обеспечивает максимальный комфорт и подвижность. В области предплечья для максимальной защиты и удобства использована пена средней плотности Основные особенности: ✅Уровень – Профессиональный ✅Защита локтя – JDP; D3O ✅Защита предплечья – Пена средней плотности ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа YTH Линейка TACKS', '/root/images/Налокотники/CCM_SUPER TACKS AS1_YTH.png', null, 'SUPER TACKS AS1', 2, 7);
