INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (248, 'JR', '', 'Описание Налокотники CCM TACKS 9040, любительская модель линейки CCM 2019 года. Данная модель предназначена для игроков-любителей. Локтевая чаша данных налокотников выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. В области предплечья для максимальной защиты и удобства используется формованный полиэтилен. Особенности: • Уровень – Любительский • Защита локтя – JDP • Защита бицепса – Формованная пена; Полиэтилен • Защита предплечья – Формованный полиэтилен • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Налокотники/CCM_TACKS 9040_JR.png', null, 'TACKS 9040', 2, 7);
