INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (250, 'YTH', '', 'Описание Налокотники BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет цельную конструкцию для защиты локтя с пенополиуретановым покрытием. Для защиты бицепса используется пена VENT ARMOR, которая придает налокотнику более легкий вес и хорошо поглощают энергию удара. Для защиты предплечья используется пена средней плотности с вставкой из полиэтилена. В данной модели используется рукав, который придает налокотнику максимальное удобство во время игры. Внутренняя подкладка выполнена по технологии THERMO MAX+, благодаря этому налокотник быстро сохнет. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пенополиуретан ✅Защита бицепса – VENT ARMOR ✅Защита предплечья – Пена средней плотности; полиэтилен ✅Крепление – Рукав; 1 V-образная липучка ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 7);
