INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (255, 'JR', '', 'Описание Налокотники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пена высокой плотности ✅Защита бицепса – Пена высокой плотности ✅Защита предплечья – Пена высокой плотности ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (256, 'SR', '', 'Описание Налокотники BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Налокотники Х900 LITE имеют клиновидную форму и состоят из 2-х частей. Локти сделана из пены HYPERLITE, которая имеет легкий вес и при этом не теряет своих защитных функций. Внутренний материал налокотника THERMO MAX обработан по технологии SANITIZED®, который предотвращает распространение бактерий и возникновения неприятных запахов. Особенности: • Уровень – Профессиональный • Посадка - Клиновидная • Защита локтя – Пена HYPERLITE • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – Литая пена HYPERLITE • Крепление – Три ремня крепления • Подкладка –THERMO MAX; SANITIZED®. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR X900 LITE_SR.png', null, 'VAPOR X900 LITE', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (257, 'SR', '', 'Описание Налокотники BAUER NEXUS 8000 профессионального уровня имеют двухсоставную, подвижную конструкцию с независимым протектором защиты бицепса, что обеспечивает максимально полный диапазон движения руки хоккеиста. Элемент защиты бицепса имеет внутреннюю набивку из пены EPP SuperLite, которая обладает наилучшими защитными характеристиками и сниженным, по сравнению с другими пеноматериалами, весом. Элемент защиты бицепса, выполненный по технологии Dome Protection, полностью армирован пластиковыми вставками, имеет сегментированное строение и обеспечивает максимально полный обхват верхней части руки. Протектор защиты предплечья соединен с локтевой чашей, выполнен из пены EPP SuperLite и дополнительно усилен ребристой, пластиковой вставкой. Предварительно спрофилированная локтевая чаша с мягким, внутренним наполнением снижает ударную нагрузку, приходящуюся на локтевой сустав при падении или столкновении, при ударах. Внутренняя подкладка выполнена из гидрофобного материала по технологии 37.5, которая позволяет игроку тратить больше энергии при движении, удаляя горячий воздух из налокотников и способствуя испарению излишней влаги, за счет повышения температуры тела хоккеиста. Система фиксации налокотника состоит из эластичного ремня шириной 3,8 см, расположенного в верхней части, нейлонового ремня шириной 5 см, фиксирующего нижнюю часть, и ремня из неопрена в средней части налокотника. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Налокотники/BAUER_NEXUS 8000_SR.png', null, 'NEXUS 8000', 1, 7);
