INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (269, 'JR', '', 'Описание Налокотники BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Одним из главных достижений разработчиков этой модели можно считать снижение веса на 25% относительно предыдущей коллекции. Благодаря легким материалам и новейших технологий налокотники обеспечивают максимальное удобство и мобильность без ущерба защите. Применение в стратегических областях материала CURV® создает эффективную защиту с минимальным весом. Особенности: • Уровень – Элитный • Посадка - Клиновидная • Защита локтя – AEROLITE 2.0 • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – CURV® • Крепление – Три ремня крепления • Подкладка –37,5®; SANITIZED®. Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR 1X LITE_JR.png', null, 'VAPOR 1X LITE', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (270, 'SR', '', 'Описание Налокотники BAUER VAPOR 1X топовая модель в линейке Vapor, новинка 2016 года обладает легкой и подвижной конструкцией, обеспечивая высокий уровень защиты полевого игрока. Налокотники имеют трехсоставную конструкцию Free Flex, которая позволяет элементам защиты бицепса, предплечья и локтевой чаше двигаться гибко и свободно друг от друга во всех направлениях. Элемент защиты бицепса имеет сегментированное строение и навесную конструкцию. В качестве внутреннего наполнения в элементах защиты бицепса используется легкая пена Aerolite, которая покрывает пластиковые вкладыши, эффективно защищая верхнюю часть руки игрока. Встроенная, локтевая чаша из формованного пластика покрыта пеной средней плотности, как с внутренней, так и с наружной стороны, образуя эффективную, двухстороннюю защиту для игрока. Внутреннее отверстие для локтевого сустава в локтевой чаше комфортно фиксирует локтевой сустав, исключая его смещение. Элемент защиты предплечья выполнен из композитного материала Curv и дополнен пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами, не впитывает влагу. Такое сочетание позволяет облегчить защитное снаряжение и обеспечить максимально высокий уровень защиты. В качестве внутренней подкладки используется гидрофобный, сетчатый материал с технологией 37.5, которая позволяет использовать тепло тела для быстрого испарения влаги. Локтевая чаша дополнительно покрыта с внутренней стороны сеткой, которая обладает хорошей воздухопроницаемостью и усиливает действие технологии 37.5, а так же обеспечивает исключительный уровень комфорт. Налокотники фиксируются на руках с помощью трех ремней крепления. Эластичный ремень шириной 2" (5 см) фиксирует элемент защиты предплечья. Эластичный ремень шириной 2" (5 см) фиксирует элемент защиты на бицепсе. Ремень из эластичной сетки Y-образной формы фиксирует локтевую чашу на сгибе локтя. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Налокотники/BAUER_VAPOR 1X_SR.png', null, 'VAPOR 1X', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (271, 'SR', '', 'Описание Налокотники BAUER SUPREME S17 1S Почувствуйте анатомическую форму нового налокотника Supreme 1S благодаря композитному материалу Curv® и технологии POWERLITE. Профилированная пена POWERLITE плотно прилегает к локтю, чтобы сохранять защиту в нужном месте. Особенности: • Общая посадка : Анатомическая форма • Локтевой сустав: FleXorb® G-FORM® and XRD®Materials • Система крепления: 3 ремня • Подкладка: 37,5® Technology и Sanitized® • Защита локтя: Curv®и POWERLITE • Защита бицепса: пена Vent Armor Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 1S_SR.png', null, 'SUPREME 1S', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (272, 'SR', '', 'Описание Налокотники WARRIOR ALPHA DX PRO, профессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • SLING WRAP - Регулируемые ремни обеспечивают надежную фиксацию и максимальное удобство • WARTECH FNC LINER – Антибактериальная подкладка Polygiene обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '/root/images/Налокотники/WARRIOR_ALPHA DX PRO_SR.png', null, 'ALPHA DX PRO', 3, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (273, 'SR', '', 'Описание Налокотники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пена высокой плотности ✅Защита бицепса – Пена высокой плотности ✅Защита предплечья – Пена высокой плотности ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME S29_SR.png', null, 'SUPREME S29', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (274, 'JR', '', 'Описание Налокотники CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Для защиты бицепса используется формованная пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O LITE. В области предплечья для максимальной защиты и удобства использована пена D3O и формованный полиэтилен. Для максимального комфорта в налокотниках используется новая инновационная технология фиксации. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; D3O LITE • Защита бицепса – Пена высокой плотности • Защита предплечья – Формованный полиэтилен; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Налокотники/CCM_TACKS 9080_JR.png', null, 'TACKS 9080', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (275, 'SR', '', 'Описание Налокотники CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет легкий вес. Для защиты бицепса используется пена высокой плотности и формованный полиэтилен. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM, которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; Формованный полиэтилен • Защита бицепса – Формованный полиэтилен • Защита предплечья – Формованный полиэтилен • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Налокотники/CCM_JETSPEED FT390_SR.png', null, 'JETSPEED FT390', 2, 7);
