INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (277, 'SR', '', 'Описание Налокотники BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Налокотники имеют улучшенное внешнее покрытие, если сравнивать с предыдущей линейкой NEXUS. Локтевая чаша выполнена из IX FOAM. В защите бицепса используется пена MDP со вставками из воздухопроницаемого пенного наполнителя VENT ARMOR, а внутренняя подкладка THERMO MAX с технологией SANITIZED®, предотвращает развитие бактерий и образование неприятного запаха. Особенности: • Уровень – Элитный • Посадка – Классическая, широкая • Защита локтя – IX FOAM • Защита бицепса – MDP; VENT ARMOR • Защита предплечья – MDP • Крепление – Три ремня крепления • Подкладка –THERMO MAX; SANITIZED®. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Налокотники/BAUER_NEXUS 2N_SR.png', null, 'NEXUS 2N', 1, 7);
