INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (282, 'SR', '', 'Описание Налокотники CCM SUPER TACKS LONG, профессиональная модель линейки TACKS 2017 года. Налокотники профессионального уровня обладают удобной посадкой и сниженным весом. Налокотники имеют двухсоставную конструкцию и традиционную посадку. Элемент защиты предплечья выполнены из пены высокой плотности и пены D3O, которая создана для эффективного поглощения ударных нагрузок и обеспечения высокого уровня защиты. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов.Для защиты бицепса используется формованная пена высокой плотности Особенности: • Уровень – Элитный • Защита локтя – JDP; D3O • Защита бицепса – Пена высокой плотности • Защита предплечья – HD FOAM; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Налокотники/CCM_SUPER TACKS LONG_SR.png', null, 'SUPER TACKS LONG', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (283, 'SR', '', 'Описание Налокотники CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Для защиты бицепса используется формованная пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O LITE. В области предплечья для максимальной защиты и удобства использована пена D3O и формованный полиэтилен. Для максимального комфорта в налокотниках используется новая инновационная технология фиксации. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; D3O LITE • Защита бицепса – Пена высокой плотности • Защита предплечья – Формованный полиэтилен; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Налокотники/CCM_TACKS 9080_SR.png', null, 'TACKS 9080', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (284, 'JR', '', 'Описание Налокотники CCM SUPER TACKS AS1, топовая модель линейки CCM 2019 года. Данные налокотники предназначены для игроков элитного уровня и сочетает в себе максимальный комфорт и защиту. Для защиты бицепса используется формованная пена высокой плотности. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O. В области предплечья для максимальной защиты и удобства использована пена высокой плотности и пена D3O. Для максимального комфорта в налокотниках используется новая инновационная технология фиксации. Особенности: • Уровень – Элитный • Защита локтя – JDP; D3O • Защита бицепса – Пена высокой плотности • Защита предплечья – HD FOAM; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Налокотники/CCM_SUPER TACKS AS1_JR.png', null, 'SUPER TACKS AS1', 2, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (285, 'JR', '', 'Описание Налокотники BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из композитного материала CURV, который придает налокотнику легкий вес и отличные защитные свойства. В области локтя используется пена PORON XRD, которая максимально поглощает энергию удара. В качестве внутренней подкладки используется материал THERMO CORE, который максимально отводит влагу. Особенности: ✅Уровень – Элитный ✅Посадка - Анатомическая ✅Защита локтя – PORON XRD ✅Защита бицепса – CURV ✅Защита предплечья – CURV ✅Крепление – Три ремня крепления ✅Подкладка –THERMO CORE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Налокотники/BAUER_SUPREME 2S PRO_JR.png', null, 'SUPREME 2S PRO', 1, 7);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (286, 'SR', '', 'Описание Налокотники CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет очень легкий вес. Для защиты бицепса используется очень легкий композитный материал ROCKETFRAME. Зона предплечья выполнена из пены высокой плотности ZOTEFOAM, которая максимально защищает ваше тело. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Элитный • Защита локтя – JDP; Формованная пена PE • Защита бицепса – ROCKETFRAME • Защита предплечья – ZOTEFOAM • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Налокотники/CCM_JETSPEED FT1_SR.png', null, 'JETSPEED FT1', 2, 7);
