INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (290, 'JR', '', 'Описание Трусы CCM TACKS 1052 модель начального уровня, обеспечивает надежную защиту и комфорт. Внешний чехол пошит из износоустойчивого нейлона и имеет эластичные вставки Flex Zones, которые позволяют защитному снаряжению свободно двигаться вместе с телом хоккеиста. Расширенный элемент защиты позвоночника выполнен из пены Comfort и имеет жесткую, пластиковую вставку для дополнительной защиты. Сегментированный элемент защиты копчика соединяется с протектором защиты позвоночника, образуя надежную защиту. Формованные элементы из пластика, покрытие вспененным материалом, создают опоясывающую защиту области почек. Для защиты тазобедренных суставов используются элементы, выполненные по технологии JDP, которая рассеивает энергию удара и перераспределяет ее от сустава к соседним мышцам. Набедренные щитки выполнены из жесткого, формованного пластика с внутренним покрытием из пены Comfort. Дополнительный слой пены Comfort и сетчатый материал используются в качестве подкладочного слоя и обеспечивают мягкую и комфортную посадку трусов. Нейлоновый ремень позволяет зафиксировать трусы на талии. Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 1052_JR.png', null, 'TACKS 1052', 2, 8);
