INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (294, 'SR', '', 'Описание Чехлы на трусы BAUER S19 SUPREME PANT SHELL, модель 2019 года. Внешний чехол выполнен из высокопрочного нейлона плотностью 840 ден. Область максимального износа дополнительно усилена. Конструкция чехла имеет регулировку по ширине штанин с помощью застежки-молнии. Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_S19 SUPREME PANT SHELL_SR.png', null, 'S19 SUPREME PANT SHELL', 1, 8);
