INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (303, 'JR', '', 'Описание Трусы CCM JETSPEED FT350, любительская модель линейки CCM 2018 года. Трусы данной модели имеют чехол, выполненный из легкого полиэстера плотностью 210 ден. Защитные функции бедра выполнены из вспененного полиэтилена. Так же вставки из вспененного полиэтилена обеспечивают высокий уровень защиты в области почек и тазобедренного сустава. Внутренняя подкладка выполнена из полиуретана, которая обеспечивает комфорт. Особенности: • Уровень – Любительский • Удобство – Зауженное • Защита позвоночника – Вставка PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – Полиуретан • Регулировка – нет • Чехол – Плотность 210 ден Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT350_JR.png', null, 'JETSPEED FT350', 2, 8);
