INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (305, 'YTH', '', 'Описание Трусы BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет классическую посадку. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Одно из нововведения в этих трусах, стало то, что их можно отрегулировать на 1 дюйм(2,5см) Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (306, 'SR', '', 'Описание Трусы BAUER NEXUS N8000. Шорты классического покроя – движение без ограничений. Обеспечьте себе необходимую защиту: формованная пена высокой плотности в области позвоночника и почек, двухсекционная эргономичная накладка, защищающая от случайных и сильных ударов. Особенности: Пояс - пена средней плотности Гибкие вставки - растягивающаяся ткань Ластовицы - растягивающиеся ластовицы со скрытой молнией Передняя вставка - система шнуровки Подкладка - THERMO MAX Дополнительные элементы - пуговицы для бандажа Позвоночник - формованная вставка из пены высокой плотности и полиэтилена Наружная часть бедра - прессованная куполообразная конструкция Почки - вставки из пеноматериала высокой плотности Внутренняя часть бедра - эргономичная защита из 2 частей Наружный материал - износостойкий нейлон 400 ден Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Трусы/BAUER_NEXUS N8000_SR.png', null, 'NEXUS N8000', 1, 8);
