INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (315, 'SR', '', 'Описание Трусы CCM SUPER TACKS 9040, любительская модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется формованный полиэтилен. В области бедра и почек для большей защиты используется пластик. Для защиты тазобедренных суставов используется конструкция JDP, которая рассеивает удар. Особенности: ✅Уровень – Любительский ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – JDP ✅Защита бедра – Пластик ✅Регулировка – Отсутствует ✅Чехол – 400 ден Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9040_SR.png', null, 'TACKS 9040', 2, 8);
