INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (317, 'SR', '', 'Описание Трусы BAUER NEXUS N9000. Наша конструкция обеспечивает классическое удобство, позволяя управлять каждым шагом, каждым движением. Формованная пена высокой плотности в области позвоночника и почек служит отличной защитой, а регулировка длины позволяет скорректировать размер шорт специально для вас. Особенности: • Пояс - Пена средней плотности с вышитыми логотипами • Гибкие вставки - Растягивающаяся ткань • Ластовицы - Растягивающиеся ластовицы со скрытой молнией • Передняя вставка - Система шнуровки • Подкладка - THERMO MAX • Дополнительные элементы - Дополнительный комфорт (+2,5 см, или 1 дюйм, к длине), крепления для подтяжек • Позвоночник - Прессованный пеноматериал высокой плотности • Наружная часть бедра - Прессованная куполообразная конструкция • Почки - Прессованный пеноматериал высокой плотности • Внутренняя часть бедра - Эргономичная защита из 2 частей • Наружный материал - Профессиональные износоустойчивые накладки плотностью 840 денье (бедро) Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Трусы/BAUER_NEXUS N9000_SR.png', null, 'NEXUS N9000', 1, 8);
