INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (320, 'JR', '', 'Описание Трусы BAUER NEXUS 1N флагманская модель в линейке Nexus 2016 года. Чехол трусов пошит из высокопрочного и легкого нейлона плотностью 840 Den. Дополнительные эластичные вставки и ластовица в чехле позволяют игроку свободно двигаться и сгибаться, а так же способствуют дополнительной воздухопроницаемости защитного снаряжения. Защитный щиток, закрывающий позвоночник, выполнен с применением технологииSeven+ и пены высокой плотности, благодаря чему формованная пена щитка эффективно поглощает силу удара, сохраняя свою первоначальную форму и свои защитные функции после многочисленных попаданий шайбой или клюшкой. Плавающая конструкция щитка обеспечивает большую подвижность защиты спины. При этом легкость используемой в щитке пены отлично сочетается с ее высокими защитными свойствами. Сегментированный элемент защиты копчика выполнен из пены высокой плотности. Над талией расположена формованная защита области почек, которая выполнена из пены высокой плотности. Тазобедренные суставы защищены куполообразными чашами, которые плотно прилегают к телу и создают надежную защиту благодаря использованию при их производстве формованного полиэтилена и пены Comfort. Эргономичные элементы защиты бедра с внутренним наполнением из пены средней плотности имеют двухсоставную конструкцию, обеспечивая максимальное покрытие и удобную посадку. Внутренняя подкладка выполнена из микрофибры с применением технологии 37.5, которая использует тепло тела хоккеиста для быстрого и эффективного испарения влаги. Специальная обработка материала подкладки устраняет появление неприятных запахов в процессе эксплуатации защиты. Широкий проложенный пеной пояс создает дополнительную защиту талии. Внизу штанин с внутренней стороны имеются дополнительные молнии, позволяющие увеличить ширину штанин. Расширенная система подгонки позволяет увеличить длину трусов на 1" (2,5 см). Трусы крепятся на теле игрока с помощью шнуровки и нейлонового пояса с прочным карабином. Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Трусы/BAUER_NEXUS 1N_JR.png', null, 'NEXUS 1N', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (321, 'JR', '', 'Описание Трусы BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX, который отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Подкладка – THERMO MAX ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (322, 'JR', '', 'Описание Трусы CCM SUPER TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется формованный полиэтилен. В области бедра и почек для максимальной защиты используется пластик. Для защиты тазобедренных суставов используется конструкция JDP, которая рассеивает удар. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – JDP ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9060_JR.png', null, 'TACKS 9060', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (323, 'SR', '', 'Описание Трусы CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Трусы данной модели имеют чехол, выполненный из текстурированного полиэстера плотностью 400 ден. Защитные функции бедра выполнены из вспененного полиэтилена. Так же вставки из вспененного полиэтилена обеспечивают высокий уровень защиты в области почек и тазобедренного сустава. Особенности: • Уровень – Полупрофессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – Влагоотталкивающий полиэстер • Регулировка – нет • Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 8);
