INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (325, 'JR', '', 'Описание Трусы WARRIOR COVERT QRE, топовая модель новой линейки ALPHA 2019 года. • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • ADJUSTABLE LEG EXTENSION – Универсальное решение для растущих игроков. Застежка молния обеспечивает возможность удлинить трусы на 1 дюйм • AXYFLEX KIDNEY AND SPINE PROTECTION – Плавающий внутренний поясной ремень обеспечивает комфорт и подвижность, а также защищает позвоночник и почки • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Характеристики Возрастная группа JR Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE_JR.png', null, 'COVERT QRE', 3, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (326, 'SR', '', 'Описание Трусы WARRIOR COVERT QRE PRO, профессиональная модель новой линейки COVERT 2019 года. Особенности: • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • ADJUSTABLE LEG EXTENSION – Универсальное решение для растущих игроков. Застежка молния обеспечивает возможность удлинить трусы на 1 дюйм • PREMIUM PROTECTION – Лучшая защитная пена и пластиковые вставки обеспечивают лучшую защиту • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Характеристики Возрастная группа SR Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE PRO_SR.png', null, 'COVERT QRE PRO', 3, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (327, 'SR', '', 'Описание Трусы BAUER VAPOR 1X топовая модель в линейке Vapor, новинка 2016 года выполнена с использованием новейших технологий и создает высокий уровень защиты, обеспечивая полевому игроку максимальную мобильность. Чехол трусов скомбинирован из прочного нейлона плотностью 840 Den и эластичного материала для обеспечения максимальной мобильности. Эластичные, хорошо вентилируемые, клинья и зоны гибкости располагаются в паховой области, вдоль внутренней части штанин и задней части трусов. Высокопрочный нейлон усиливает боковые части трусов, располагаясь вдоль бедер. Защитный щиток, закрывающий позвоночник, имеет конструкцию Free Flex Spine и слегка наклонен вперед, что позволяет ему двигаться независимо от других элементов защиты, обладая высокой подвижностью и всегда находиться там, где он должен быть. Этот защитный щиток выполнен из композитного материала Curv, дополнен пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами, не впитывает влагу, и армирован пластиковой вкладкой. Такое сочетание позволяет облегчить защитное снаряжение и обеспечить максимально высокий уровень защиты. Аналогичное строение с использованием сочетания композитного материала Curv и пены Aerolite имеют защитные элементы, закрывающие область почек, копчик, область таза и тазобедренных суставов. Щитки, закрывающие бедра, имеют эргономичную конструкцию, и так же выполнены из композитного материала Curv и пены Aerolite, что создает надежную и легкую защиту. По внутренней стороне трусов проходит регулируемый, внутренний, нейлоновый пояс, который усилен пеной средней плотности. На талии, под поясом располагается молния, раскрыв которую можно увеличить длину трусов на 1" (2,5 см), что делает данную модель особенно удобной для высоких игроков. Внутренняя, гидрофобная подкладка из сетчатого материала выполнена с применением технологии 37.5, которая позволяет использовать тепло тела для быстрого испарения влаги. А так же подкладка обладает противоскользящими свойствами и не поглощает запахи. На внутренней стороне штанин внизу расположены молнии. Трусы фиксируются на теле с помощью нейлонового ремня с прочным карабином и шнуровки. Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы/BAUER_VAPOR 1X_SR.png', null, 'VAPOR 1X', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (328, 'SR', '', 'Описание Трусы BAUER NEXUS 1N флагманская модель в линейке Nexus 2016 года. Чехол трусов пошит из высокопрочного и легкого нейлона плотностью 840 Den. Дополнительные эластичные вставки и ластовица в чехле позволяют игроку свободно двигаться и сгибаться, а так же способствуют дополнительной воздухопроницаемости защитного снаряжения. Защитный щиток, закрывающий позвоночник, выполнен с применением технологииSeven+ и пены высокой плотности, благодаря чему формованная пена щитка эффективно поглощает силу удара, сохраняя свою первоначальную форму и свои защитные функции после многочисленных попаданий шайбой или клюшкой. Плавающая конструкция щитка обеспечивает большую подвижность защиты спины. При этом легкость используемой в щитке пены отлично сочетается с ее высокими защитными свойствами. Сегментированный элемент защиты копчика выполнен из пены высокой плотности. Над талией расположена формованная защита области почек, которая выполнена из пены высокой плотности. Тазобедренные суставы защищены куполообразными чашами, которые плотно прилегают к телу и создают надежную защиту благодаря использованию при их производстве формованного полиэтилена и пены Comfort. Эргономичные элементы защиты бедра с внутренним наполнением из пены средней плотности имеют двухсоставную конструкцию, обеспечивая максимальное покрытие и удобную посадку. Внутренняя подкладка выполнена из микрофибры с применением технологии 37.5, которая использует тепло тела хоккеиста для быстрого и эффективного испарения влаги. Специальная обработка материала подкладки устраняет появление неприятных запахов в процессе эксплуатации защиты. Широкий проложенный пеной пояс создает дополнительную защиту талии. Внизу штанин с внутренней стороны имеются дополнительные молнии, позволяющие увеличить ширину штанин. Расширенная система подгонки позволяет увеличить длину трусов на 1" (2,5 см). Трусы крепятся на теле игрока с помощью шнуровки и нейлонового пояса с прочным карабином. Характеристики Возрастная группа SR Линейка NEXUS', '/root/images/Трусы/BAUER_NEXUS 1N_SR.png', null, 'NEXUS 1N', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (329, 'JR', '', 'Описание Трусы BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 840 ден Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME 2S_JR.png', null, 'SUPREME 2S', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (330, 'SR', '', 'Описание Трусы CCM SUPER TACKS 9060, полупрофессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется формованный полиэтилен. В области бедра и почек для максимальной защиты используется пластик. Для защиты тазобедренных суставов используется конструкция JDP, которая рассеивает удар. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Полиэтилен ✅Защита почек – Пластик ✅Защита тазобедренная – JDP ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9060_SR.png', null, 'TACKS 9060', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (331, 'SR', '', 'Описание Трусы BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Благодаря использованию в трусах новых материалов и усовершенствованных технологий вес был снижен на 20% относительно прошлой линейки. Пена HYPERLITE, AEROLITE 2.0 и композит CURV® обеспечат вам максимальное удобство и легкость без ущерба для защиты. Так же трусы имеют высокопрочный чехол, выполненный из нейлона профессионального уровня плотностью 840 ден. Для вашего удобства в трусах предусмотрена система регулировки, которая позволяет увеличить трусы на 1 дюйм. Особенности: • Уровень – Элитный • Удобство – Клиновидное • Защита позвоночника – CURV®; FREE FLEX • Защита почек – Пена HYPERLITE • Защита тазобедренная – AEROLITE 2.0 • Защита бедра – Пена HYPERLITE • Подкладка – 37.5®; SANITIZED® • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Трусы/BAUER_VAPOR 1X LITE_SR.png', null, 'VAPOR 1X LITE', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (332, 'JR', '', 'Описание Трусы CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Данные трусы имеют очень легкий вес и зауженную посадку, для лучшей мобильности и комфорта. В стратегически важных местах, таких как, тазобедренные суставы, позвоночник, бедро используется полиэтилен, который максимально защищает от ударов. Внутренняя подкладка DRYFORM, которая эффективно отталкивает влагу. Чтобы подобрать идеальную форму шорты имеют регулируемую посадку и могут удлиняться на 1« (2,5 см) за счет вшитой под поясом молнии. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Особенности: • Уровень – Профессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – DRYFOAM • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден; усиленные вставки в области бедра Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (333, 'SR', '', 'Описание Трусы BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены средней плотности и пластиковых вставок. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX, который отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Полупрофессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Подкладка – THERMO MAX ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME S29_SR.png', null, 'SUPREME S29', 1, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (334, 'JR', '', 'Описание Трусы CCM SUPER TACKS 9080, профессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется плавающая конструкция с пеной D3O LITE, которая рассеивает энергию удара, эта же пена используется для защиты тазобедренных суставов тела. В области бедра для максимальной защиты используется пена высокой плотности и пластик. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – D3O LITE ✅Защита почек – Пластик ✅Защита тазобедренная – JDP; D30 LITE ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9080_JR.png', null, 'TACKS 9080', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (335, 'SR', '', 'Описание Трусы WARRIOR COVERT QRE, топовая модель новой линейки ALPHA 2019 года. • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • ADJUSTABLE LEG EXTENSION – Универсальное решение для растущих игроков. Застежка молния обеспечивает возможность удлинить трусы на 1 дюйм • AXYFLEX KIDNEY AND SPINE PROTECTION – Плавающий внутренний поясной ремень обеспечивает комфорт и подвижность, а также защищает позвоночник и почки • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Характеристики Возрастная группа SR Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE_SR.png', null, 'COVERT QRE', 3, 8);
