INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (337, 'SR', '', 'Описание Трусы CCM SUPER TACKS 9080, профессиональная модель линейки TACKS 2019 года. Данная модель имеет анатомическую посадку. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Для защиты позвоночника используется плавающая конструкция с пеной D3O LITE, которая рассеивает энергию удара, эта же пена используется для защиты тазобедренных суставов тела. В области бедра для максимальной защиты используется пена высокой плотности и пластик. Данная модель имеет систему регулировки на 1 дюйм(2,5см). Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – D3O LITE ✅Защита почек – Пластик ✅Защита тазобедренная – JDP; D30 LITE ✅Защита бедра – Пластик ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 400 ден Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Трусы/CCM_TACKS 9080_SR.png', null, 'TACKS 9080', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (338, 'JR', '', 'Описание Трусы CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Данные трусы имеют очень легкий вес и зауженную посадку, для лучшей мобильности и комфорта. В защите позвоночника используется очень легкий композитный материал ROCKETFRAME. Защита бедер выполнена из пены высокой плотности и вставок из формованного полиэтилена, для максимальной защиты. Внутренняя подкладка DRYFORM, которая эффективно отталкивает влагу. Чтобы подобрать идеальную форму шорты имеют регулируемую посадку и могут удлиняться на 1« (2,5 см) за счет вшитой под поясом молнии. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Особенности: • Уровень – Элитный • Удобство – Зауженное • Защита позвоночника – ROCKETFRAME • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Пена высокой плотности; Формованные вставки PE • Подкладка – DRYFOAM • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден; усилен боковыми вставками в области бедра Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Трусы/CCM_JETSPEED FT1_JR.png', null, 'JETSPEED FT1', 2, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (339, 'SR', '', 'Описание Трусы WARRIOR COVERT QRE PRO GIRDLE, топовая модель линейки COVERT 2018 года. Особенности: • COVERT TAPER FIT – Крой с учетом специфики движения хоккеиста на коньках • AXYFLEX KIDNEY AND SPINE PROTECTION – Плавающий внутренний поясной ремень обеспечивает комфорт и подвижность, а также защищает позвоночник и почки • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности • WARTECH FNC LINER – Специальная подкладка для того, чтобы игрок оставался сухим и не появлялся неприятный запах Чехол для трусов WARRIOR COVERT QRE PRO GIRDLE, линейки COVERT 2018 года Особенности: • COVERT TAPER FIT – Удобная посадка, которая не ограничивает свободу движения • PREMIUM INTEGRATION – Идеальное сочетание с командными цветами • MINIMUS OUTER SHELL – Нейлоновая тянущаяся ткань для меньшего веса и долговечности Характеристики Возрастная группа SR Линейка COVERT', '/root/images/Трусы/WARRIOR_COVERT QRE PRO GIRDLE_SR.png', null, 'COVERT QRE PRO GIRDLE', 3, 8);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (340, 'SR', '', 'Описание Трусы BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Защитные щитки, закрывающие бедра имеют монолитную конструкцию. Внешний материал трусов выполнен из износостойкого нейлона плотностью 840 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 840 ден Характеристики Возрастная группа SR Линейка SUPREME', '/root/images/Трусы/BAUER_SUPREME 2S_SR.png', null, 'SUPREME 2S', 1, 8);
