INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (389, 'JR', '', 'Описание Перчатки CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Благодаря внешнему покрытию перчаток из высококачественного полиэстера и искусственной кожи перчатки очень прочны и износостойки. В перчатки используется гибкая манжета для лучшей подвижности руки. Тыльная сторона перчатки для максимальной защиты, выполнена из трех разных технологий. В первом слое использована пена премиум класса ZOTEFOAM, которая не впитывает влагу и сохраняет перчатки легкий вес. Следующий слой выполнен из пены D3O, которая обладает высокой степенью поглощения удара. И третий слой это пластиковые вставки PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала PRO PALM CLARINO, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал пенополиуретан PU выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Внешний материал – Искусственная кожа; высококачественный полиэстер • Защита тыльной стороны – D3O; ZOTEFOAM; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – PRO PALM CLARINO • Подкладка – Полиуретан PU;POLYGIENE Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT1_JR.png', null, 'JETSPEED FT1', 2, 11);
