INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (393, 'SR', '', 'Описание Перчатки CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Перчатки TACKS 9080 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из очень легкого и прочного материала PRO SELECTED POLYESTER. Защита пальцев и тыльной стороны перчатки выполнена из пены высокой плотности, технологии D3O,которая максимально поглощает энергию, и полиэтиленовых вставок. Ладонь выполнена из материала PALM CLARINO, которая обладает максимальным удобством, а дополнительная накладка из армированного кевлара, обеспечивает прочность. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – PRO SELECTED POLYESTER • Защита тыльной стороны – D3O; Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –PALM CLARINO • Подкладка – POLYGIENE Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9080_SR.png', null, 'TACKS 9080', 2, 11);
