INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (411, 'YTH', '', 'Описание Перчатки BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки. Манжета имеет сегментированное строение, благодаря чему ребенок может двигать запястьем свободно. Для максимального удобства и защиты тыльная сторона перчатки имеет сегментированное строение из пены средней плотности. Пальцы у перчаток имеют двухсоставную конструкцию, обеспечивая максимальный комфорт во время движения. Ладонь, выполненная из материала NASH с вставками из не скользящего материала в области пальцев. Внутренняя подкладка THERMO MAX+ сохраняют руки в сухости и придают максимальный комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Внешний материал – Плетеная сетка CABLE MESH; Нейлон ✅Защита тыльной стороны – Пена средней плотности ✅Защита пальцев – Пена средней плотности ✅Большой палец – Цельный, неподвижный ✅Ладонь – NASH ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME 2S PRO_YTH.png', null, 'SUPREME 2S PRO', 1, 11);
