INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (416, 'SR', '', 'Описание Перчатки CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Данная модель выполнена из высококачественного сетчатого материала PRO MESH, который придает перчаткам прочность и легкость. Для защиты тыльной части и пальцев используется вставки из полиэтилена и пластика. Материал ладошки NASH с дополнительной накладкой. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Высококачественный сетчатый материал PRO MESH • Защита тыльной стороны – Полиэтилен; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – NASH • Подкладка – Полиуретан PU Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT370_SR.png', null, 'JETSPEED FT370', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (417, 'JR', '', 'Описание Перчатки CCM TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Перчатки TACKS 9060 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из легкого и прочного полиэстера. Защита пальцев и тыльной стороны перчатки выполнена из пены двойной плотности, и полиэтиленовых вставок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Полиэстер • Защита тыльной стороны –Пена двойной плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –NASH • Подкладка – POLYGIENE Характеристики Возрастная группа JR Линейка TACKS', '/root/images/Перчатки/CCM_TACKS 9060_JR.png', null, 'TACKS 9060', 2, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (418, 'SR', '', 'Описание Перчатки EAGLE X905. Особенности: • Три части боковой защиты указательного пальца • Усиленная пластиком тыльная часть перчатки • Конструкция разделенных пальцев добавляет гибкость • Угловые задние ролики для дополнительной гибкости • Ручная работа с вышитым принтом • Верх перчатки из материала Tuff-Tek для легкости • Неподвижный большой палец • Новая ладонь MSH3 со вставками • Ручная работа сделано в Канаде Характеристики Возрастная группа SR', '/root/images/Перчатки/EAGLE_X905_SR.png', null, 'X905', 15, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (419, 'SR', '', 'Описание Перчатки TRUE XC5, полупрофессиональная модель линейки XC 2018 года. Данная модель перчаток выполнена из высококачественного нейлона, сочитающий в себе высокую гибкость, износостойкость и воздухопроницаемость. Ладошка выполнена из материала CLARINO, который обеспечивает хорошую износостойкость и отличные тактильные свойства. Дя максимальной защиты в области пальцев и тыльной стороны перчатки используется пена двойной плотности EVA с вставками из пластика.Внутренняя подкладка выполнена из мягкого материла, который обеспечивает приятный контакт с кожей и отлично отводит влагу с ее поверхности. Технология MICROBAN эффективно борется с ростом бактерий, что снижает вероятность появление неприятного запаха. Особенности: ✅Уровень – Полупрофессиональная ✅Внешний материал – Высококачественный нейлон ✅Защита тыльной стороны – Пена двойной плотности EVA; Вставки из пластика PE ✅Большой палец – Двухсекционный ✅Ладонь – CLARINO ✅Подкладка – MICROBAN Характеристики Возрастная группа SR', '/root/images/Перчатки/TRUE_XC5 2018_SR.png', null, 'XC5 2018', 16, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (420, 'SR', '', 'Описание Перчатки BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Модель имеет клиновидную посадку, которая отличается плотным прилеганием в области пальцев и более свободным в области манжеты. Внешний материал перчатки сделан из высокопрочной нейлоновой сетки PRO NYLON MESH и CABLE MESH . Внутренняя подкладка THERMO MAX обработанная по технологии SANITIZED®, которая препятствует возникновению неприятных запахов и распространению бактерий. Особенности: • Уровень – Профессиональный • Удобство – Клиновидное • Внешний материал – Высокопрочная нейлоновая сетка; плетеная сетка • Защита тыльной стороны – Пена двойной плотности • Защита пальцев – Пена двойной плотности • Большой палец – Запатентованная технология FLEX-LOCK ( состоит из 2-х частей ) • Ладонь – QUATTRO GRIP AX SUEDE • Подкладка – THERMO MAX; SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '/root/images/Перчатки/BAUER_VAPOR X900 LITE_SR.png', null, 'VAPOR X900 LITE', 1, 11);
