INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (424, 'JR', '', 'Описание Перчатки BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала AX SUEDE QUATTRO, обеспечивает вам комфорт и позволяет лучше чувствовать клюшку. Внутренняя подкладка THERMO MAX и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Полупрофессиональный • Удобство – Анатомическое • Внешний материал – Плетеная сетка CABLE MESH; Нейлон • Защита тыльной стороны – Пена двойной плотности; вставки из пластика • Защита пальцев – Пена средней плотности; вставки из пластика • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 2-х частей) • Ладонь – AX SUEDE QUATTRO • Подкладка – THERMO MAX; HYPERSENSE Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME S29_JR.png', null, 'SUPREME S29', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (425, 'JR', '', 'Описание Перчатки BAUER SUPREME S17 1S Удобные, мобильные, с отличной защитой: новые перчатки SUPREME 1S отлично подойдут игрокам, которые играют с напором. Анатомическая форма позволяет перчатке плотно прилегать к руке, создавая естественные тактильные ощущения, а технология FleXorb обеспечивает непревзойденный уровень защиты. В области ладони и в прокладке использован компонент Sanitized® для устранения неприятных запахов. Свежие перчатки — свежие силы в игре. Особенности: • Анатомическая форма • Трехкомпонентная часть для указательного пальца • Ладонь AX SUEDE™ QUATTRO Pro с компонентом Sanitized® для удаления неприятных запахов • FleXorb — это комбинация революционных технологий в основе G-FORM® и материала XRD® • Манжета Free Flex • Технология 37,5® с компонентом Sanitized® для устранения запахов Характеристики Возрастная группа JR Линейка SUPREME', '/root/images/Перчатки/BAUER_SUPREME 1S_JR.png', null, 'SUPREME 1S', 1, 11);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (426, 'JR', '', 'Описание Перчатки CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Тыльная сторона перчатки для максимальной защиты, выполнена из пены высокой плотности и пластиковых вставок PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала CLARINO с дополнительной вставкой, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – Высококачественный полиэстер • Защита тыльной стороны – Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – CLARINO • Подкладка – Полиуретан PU; POLYGIENE Характеристики Возрастная группа JR Линейка JETSPEED', '/root/images/Перчатки/CCM_JETSPEED FT390_JR.png', null, 'JETSPEED FT390', 2, 11);
