INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (436, 'JR', '', 'Описание Клюшка EASTON STEALTH C3.0 GRIP модель любительская уровня в обновленной линейке Stealth имеет конструкцию Fused Two-Piece. Материал, из которого изготавливается данная модель, композитная смесь EC10 Carbon Fiber Matrix, представляющая собой сплав стекловолокна и универсально выровненных углеродных волокон Uni-Carbon, и обеспечивающая этой клюшке хороший баланс, прочность и гибкость. Профиль клюшки Elliptical Taper Profile придает клюшке отличную торсионную жесткость, что увеличивает скорость полета шайбы, и точность и силу броска. Клюшка с ультранизкой точкой прогиба Ultra-Low Kick Point позволяет хоккеисту бросать молниеносно быстро. Геометрия трубы с прямыми боковыми стенками и округленными углами, а так же цепкое противоскользящее покрытие делают клюшку более хваткой и легко управляемой. Технология Micro-Bladder, так же применяемая при изготовлении крюка, усиливает чувство шайбы, повышая информативность крюка. Мультиреберная конструкция крюка Multi-Rib с продольно расположенными от пятки к носку внутренними ребрами жесткости Carbon I-beams обеспечивает мощный и точный бросок. Сегментированный внутренний сердечник из прочного полиуретана, размещенный между ребрами жесткости, придает крюку оптимальную жесткость, прочность и эффективно поглощает вибрации, возникающие при контакте крюка с шайбой. Характеристики Возрастная группа JR Линейка STEALTH', '/root/images/Клюшка/EASTON_STEALTH C3.0 GRIP_JR.png', null, 'STEALTH C3.0 GRIP', 12, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (437, 'JR', '', 'Описание Клюшка ЗАРЯД К52 JR Рукоятка: • Конструкция рукоятки сделана из высококачественного стекловолокна. • Равномерное распределение жесткости вдоль рукоятки позволяет добиться максимально простой и эффективной загрузки при выполнении броска. • Продвинутая монолитная конструкция для оптимизации баланса клюшки и лучшей эффективности передачи энергии (технология Монолит). • Традиционная прямоугольная геометрия рукоятки с вогнутыми стенками для максимально надежного хвата. • Обновленное противоскользящее покрытие. Крюк: • Оптимизированная конструкция продольных ребер жесткости (технология Мультиребра) и усиления по периметру (технология Защищенный периметр) для повышения показателей жесткости крюка. • Обновленная Ультрапена для наилучших эксплуатационных характеристик и снижения веса. Длина производителя - 52 дюйма Полная длина клюшки - 145 см Характеристики Возрастная группа JR', '/root/images/Клюшка/ЗАРЯД_52_JR.png', null, '52', 17, 13);
