INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (439, 'JR', '', 'Описание Клюшка BAUER NEXUS S16 N6000 GRIPTAC Благодаря технологии зоны Sweet Spot игроки могут стремиться к большим победам и забивать больше голов, раз за разом отправляя «сложные» шайбы в ворота. Профиль прогиба: • Профиль прогиба посередине NEXUS TRU Mid-Kick Конструкция/технология изготовления рукоятки: • Конструкция из композитного углепластика • Выгнутые с двух сторон стенки • Единая конструкция из 2 частей. Рукоятка клюшки приварена к крюку • Двойное сужение Power Profile Dual Taper • Технология зоны наилучшего восприятия Конструкция/технология изготовления крюка: • Наружная часть: Текстурный плетеный углепластик крюка • Внутренняя часть: основа крюка из полиуретана • Крюк профессионального профиля Форма рукоятки: • Выгнутые с двух сторон стенки Shaft Taper: • Power Profile Dual Taper Варианты захвата: • Покрытие GRIPTAC Характеристики Возрастная группа JR Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS S16 N6000 GRIPTAC_JR.png', null, 'NEXUS S16 N6000 GRIPTAC', 1, 13);
