INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (457, 'SR', '', 'Описание Клюшка WARRIOR COVERT QRE5, любительская модель линейки COVERT 2018 года. Особенности: • EDGE QUICK RELEASE – Клюшки данной серии имеют максимально низкую точку прогиба SUPER LOW KICK для быстрых и резких бросков • EDGE TAPER – Уникальный профиль клюшки обеспечивает один из самых быстрых и точных бросков, благодаря ромбовидной форме сужения клюшки • MINIMUS CARBON 400 – Прочная и надежная конструкция из цельнолитого углеродистого волокна. Новая геометрия края клюшки в момент броска, мгновенно и точно передает энергию на крюк, бросок получается более резкий и мощный. • TRUE 1 PHANTOM – Клюшка имеет полностью монолитную конструкцию и отличается хорошей сбалансированностью и прочностью, увеличивает чувство владения клюшкой во время игры, дает вам больше контроля и реакции Полная длина клюшки - 165 см Характеристики Возрастная группа SR Линейка COVERT', '/root/images/Клюшка/WARRIOR_COVERT QRE5 GRIP_SR.png', null, 'COVERT QRE5 GRIP', 3, 13);
