INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (459, 'YTH', '', 'Описание Клюшка WARRIOR ALPHA DX, топовая модель линейки ALPHA 2019 года. Особенности: ✅ SABER TAPER – Уникальная конструкция клиновидного сужения создает жесткую к скручиванию зону прогиба клюшки, что делает крюк более стабильным для большой точности удара. Улучшенная конструкция легко задействует прогиб и обеспечивает быстрый высокоточный релиз. ✅ MINIMUS CARBON 1400 – Карбоновая конструкция с плоским плетением, благодаря чему клюшка стала более надежной и легкой. Кроме того, этот материал стал более динамичным и эластичным. ✅ TRUE 1 PHANTOM – Улучшенный баланс и отклик заметно повышают чувствительность и контроль шайбы. Самая легкая и мощная цельнолитая клюшка TRUE 1 ✅ FUELCORE – Легкий полимерный сердечник крюка усиливает ощущение шайбы и делает броски сильнее. Более прочные и жесткие материалы увеличивают долговечность крюка. ✅ ERGO SHAFT SHAPE – Эргономичная форма шафта клюшки удобно и надежно ложится в руки, повышая чувствительность, чтобы вы могли легко совершать удары, финты и убирать шайбу под себя. ✅ APEX GRIP TEXTURE – Текстурированное нескользящее покрытие клюшки обеспечивает максимальный контроль и ощущение. Полная длина клюшки - 132 см Характеристики Возрастная группа YTH Линейка ALPHA', '/root/images/Клюшка/WARRIOR_ALPHA DX GRIP_YTH.png', null, 'ALPHA DX GRIP', 3, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (460, 'JR', '', 'Описание Клюшка BAUER NSX, любительская модель линейки NSX 2018 года. Клюшка выполнена из углеродного однонаправленного волокна. Волокна карбона в конструкции имеют повышенное натяжение и нити не переплетены между собой, а уложены в одном направлении. Данная модель имеет среднюю точку прогиба. Внешнее покрытие крюка выполнено из углеродного однонаправленного волокна. Полиуретановый сердечник крюка обеспечивает отличное чувство шайбы, уменьшает уровень вибрации при ударах и увеличивает прочность крюка. Конструкция клюшки двухсоставная. Длина производителя - 52 дюймов Полная длина клюшки - 145 см Особенности: ✅Уровень – Любительский ✅Точка прогиба – Средняя ✅Технология трубы – Карбон UD ✅Технология крюка – Карбон UD, BRIDGECORE TECHNOLOGY ✅Конструкция – двухсоставная ✅Геометрия шафта – Квадратная Характеристики Возрастная группа JR', '/root/images/Клюшка/BAUER_NSX GRIPTAC_JR.png', null, 'NSX GRIPTAC', 1, 13);
