INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (491, 'JR', '', 'Описание Клюшка BAUER VAPOR 2X TEAM, полупрофессиональная модель линейки VAPOR 2019 года. Данная модель имеет нижнюю точку прогиба. Труба клюшки выполнена из углеродного волокна CARBON UD. Конструкция трубы MICRO FEEL II с двояковогнутыми боковыми стенками и скругленными углами, обеспечивает игроку максимальное удобство. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Крюк клюшки выполнен по технологии MaxBalance, благодаря чему повышается прочность крюка. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – CARBON UD; Micro Feel II • Технология крюка – 3K CARBON; MaxBalance; AERO FOAM I • Конструкция – HIGH PERFORMANCE MOLD • Геометрия шафта – С закругленными краями Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 2X TEAM GRIP_JR.png', null, 'VAPOR 2X TEAM GRIP', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (492, 'INT', '', 'Описание Клюшка BAUER NEXUS N2700, полупрофессиональная клюшка в линейки NEXUS. Благодаря своей конструкции FUSED 2-PIECE клюшка обладает высокой надежностью и прочностью. При изготовлении клюшки используется материал карбона UD. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT ,что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. Крюк изготовлен из материала BIMAX 3K. Длина производителя - 57 дюймов Полная длина клюшки - 158 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – UD CARBON • Технология крюка – BIMAX 3K; AERO FOAM I; MAXBALANCE • Конструкция –2-х составная Характеристики Возрастная группа INT Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS N2700 GRIPTAC_INT.png', null, 'NEXUS N2700 GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (493, 'YTH', '', 'Описание Клюшка BAUER NEXUS 2N PRO, топовая клюшка линейки NEXUS. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX, которая увеличивает прочность и долговечность. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. В конструкции крюка используется углеродное волокно с плетением TeXtreme. Так же в данном крюке применена технология ADVANCED CARBON LAYERING, которая позволила уменьшить вес крюка и повысить долговечность. Длина производителя - 46 дюймов Полная длина клюшки - 129 см Особенности: • Уровень – Элитный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – TeXtreme; DURAFLEX(INT-SR) • Технология крюка - ACL; R-LITE; eLASTech(INT-SR) • Конструкция – MONOCOMP Характеристики Возрастная группа YTH Линейка NEXUS', '/root/images/Клюшка/BAUER_NEXUS 2N PRO GRIPTAC_YTH.png', null, 'NEXUS 2N PRO GRIPTAC', 1, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (494, 'YTH', '', 'Описание Клюшка BAUER VAPOR FLYLITE YTH, детская топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE YTH! Новая топовая модель является преемником модели VAPOR 1X LITE. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется углеродное волокно CARBON UD, благодаря чему клюшка стала более прочнее и легче. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию. Крюк клюшки выполнен из композитного материала 15K. Внутренний сердечник имеет пену AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 46 дюймов Полная длина клюшки - 129 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология броска – XE TAPER ✅Технология трубы – CARBON UD ✅Технология крюка – Композит 15K; AERO FOAM I ✅Конструкция – Двухсоставная ✅Геометрия шафта – С закругленными краями Характеристики Возрастная группа YTH Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR FLYLITE GRIP_YTH.png', null, 'VAPOR FLYLITE GRIP', 1, 13);
