INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (501, 'JR', '', 'Описание Клюшка BAUER VAPOR 1X GRIPTAC Самая передовая и технологичная клюшка VAPOR на сегодняшний день. Созданная для молниеносных атак, VAPOR 1X с новой технологией Quick-Release Taper подарит вам новые ощущения на льду. Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: AERO SENSE 2 Core - NEW • Профиль крюка Pure Shot Форма рукоятки: • Круглая двояковогнутая рукоятка Конусность рукоятки • Технология сужения QRT (только SR и INT) - NEW Варианты захвата • Покрытие GRIPTAC • Также доступны варианты с матовой отделкой без зоны захвата Grip Характеристики Возрастная группа JR Линейка VAPOR', '/root/images/Клюшка/BAUER_VAPOR 1X LE GRIPTAC_JR.png', null, 'VAPOR 1X LE GRIPTAC', 1, 13);
