INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (505, 'SR', '', 'Описание Клюшка CCM JETSPEED PRO2, профессиональная модель линейки JETSPEED 2019 года. Клюшка имеет гибридную точку прогиба, благодаря этому клюшка подходит, как для резких кистевых, так и для мощных бросков. Данная модель выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует прочность и лучшую стабильность при эксплуатации. Крюк клюшки JS4 BLADE имеет сверхлегкую конструкцию с амортизированный сердечником и усиленной пяткой и носком. Особенности: ✅Уровень – ПРОФЕССИОНАЛЬНЫЙ ✅Точка прогиба – Гибридная ✅Технология трубы – X-FLOW ✅Технология крюка – JS4 BLADE ✅Материал – Углеродное волокно Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Характеристики Возрастная группа SR Линейка JETSPEED', '/root/images/Клюшка/CCM_JETSPEED PRO2 GRIP_SR.png', null, 'JETSPEED PRO2 GRIP', 2, 13);
INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (506, 'SR', '', 'Описание Клюшка CCM SUPER TACKS PRO, профессиональная модель линейки CCM 2018 года. Клюшка имеет среднюю точку прогиба, обеспечивая лучшую стабильность и контроль во время мощных бросков. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует прочность и лучшую стабильность при эксплуатации. Крюк ACU3, имеет жесткую, но при этом легкую конструкцию с тактильной поверхность для максимального контроля во время броска. Особенности: ✅Уровень – Профессиональный ✅Точка прогиба – Средняя ✅Технология трубы – X-FLOW ✅Технология крюка – ACU3 ✅Материал - Композит Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см ☑105 FLEX – Длина производителя - 62 дюймов; полная длина - 175 см Характеристики Возрастная группа SR Линейка TACKS', '/root/images/Клюшка/CCM_SUPER TACKS PRO GRIP_SR.png', null, 'SUPER TACKS PRO GRIP', 2, 13);
