INSERT INTO public.product (product_id, age, characteristics, description, image_link, link, model, brand_id, type_id) VALUES (510, 'INT', '', 'Описание Клюшка BAUER SUPREME TOTALONE MX3 GRIPTAC Топовая модель коллекции 2015 года. SUPREME TOTALONE МХ3 — клюшка профессионального уровня для бросков с максимальной амплитудой. Верхняя точка прогиба в сочетании с мягким крюком обеспечат невероятную силу броска и отличный баланс. Профиль прогиба: • Усиленный профиль прогиба SUPREME посередине Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: Основа крюка POWER SENSE CORE 2 - НОВИНКА • Профиль крюка Pure Shot Форма рукоятки: • Квадратная двояковогнутая рукоятка • Конусность рукоятки • Двойное сужение SUPREME Power Варианты захвата: • Покрытие GRIPTAC Характеристики Возрастная группа INT Линейка SUPREME', '/root/images/Клюшка/BAUER_SUPREME TOTALONE MX3 GRIPTAC_INT.png', null, 'SUPREME TOTALONE MX3 GRIPTAC', 1, 13);
